css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wissenschaft.de") {

/* content */
  body {margin:0 !important;overflow-x:auto !important;width:auto !important}
  #ausrichtung {padding-top:0px !important;max-width:90% !important;width:90% !important;margin:0 auto !important;min-width:0 !important}
  #koerper {top:0px !important;width:100% !important;margin:0 auto !important;background-image:none !important}
  div.rubrik {font-weight:500 !important}

  #links {background-image:none !important;width:100% !important}
    #linkespalte  {margin:0 !important;width:20% !important;float:left !important}
    #mitte        {margin:0 !important;width:75% !important;float:left !important;padding-top:9px}
      #navidetail {margin:0 !important;width:15% !important;float:right !important}
      div.content {margin:0 !important;width:75% !important;float:left !important}

  #headunten,
  #fusszeile {height:auto !important;width:100% !important;margin:0 auto !important;max-width:100% !important;overflow:hidden !important}

/* nav */
  #linkespalte {background-image:none !important}
  #linkespalte ul li a {}
  #linkespalte * {background:none !important;color:#000 !important;}
  #linkespalte ul.specialnavigation a {padding-left:6px !important}
  #suche {background-image:none !important}

/* more news */
  #navidetail li span {font-weight:500 !important}
  #navidetail span.klein {opacity:0.4 !important}
  #navidetail:hover span.klein {opacity:1 !important}

/* ads */
  #skyscraper,
  div.content > div,
  #headoben,
  #rechts,
  #superbanner {display:none !important}

}
