css:
@-moz-document domain("getpocket.com") {

    /*
     * Name:    Pocket (OLED Dark Black Night)
     * Author:  Danny Allen (dannya.com)
     * Version: v1.0.1
     */

    /* global */
    body {
        background: #000000 !important;
        color: #808080;
    }

    .overflow-nav {
        background: #000000;
    }
    .overflow-nav .full-nav {
        border: 1px solid #808080;
    }

    .header-global nav .btn {
        background: #000000;
        color: #ffffff;
        text-shadow: none;
    }
    .header-global nav .btn:focus,
    .header-global nav .btn:hover {
        background: #222222;
    }

    .nav-bottom {
        bottom: 64px;
    }

    .notifications-error, 
    .notifications-sync {
        background: #000000;
        color: #ffffff;
    }


    /* home */
    .home-midlower-content {
        background: #000000;
    }
    .home-mid-content .home-saveforlater h3 {
        color: #ffffff;
    }
    .page-home h4 {
        color: #ffffff;
    }
    .home-midlower-content .home-startsaving .divider {
        background: #000000;
        border-top: 1px solid #808080;
    }

    .home-bottom-content {
        background: #000000;
    }
    .home-news p {
        text-shadow: none;
    }
    .home-sociallink .ribbon-right {
        background: none;
    }

    .home-explore-topic-content h2 {
        color: #ffffff;
    }


    /* support */
    .navbar .navbar-inner {
        background: #000000;
        border-bottom: 1px solid #ffffff;
    }

    #docsSearch {
        background: #000000;
    }

    .most-pop-articles .popArticles li a {
        color: #ffffff;
    }

    .category-list h3 {
        color: #ffffff;
    }
    .category-list .category {
        background: #000000;
    }
    .category-list .category:focus,
    .category-list .category:hover {
        background: #222222;
    }


    /* apps */
    #header {
        background: #000000;
        border-bottom: 1px solid #ffffff;
    }
    #header #nav li a {
        border-top-color: #808080;
    }
    #header #nav li.login a {
        margin-top: 4px;
        border: 1px solid #ffffff;
        background: no-repeat;
        border-radius: 3px;
        text-shadow: none;
        color: #ffffff !important;
    }

    .content-welcome {
        background: #000000;
    }
    .content-welcome h2 {
        color: #ffffff;
    }

    .content-welcome .large-5:first-child {
        display: none;
    }
    .content-welcome .large-5:nth-child(2) {
        width: 100% !important;
        padding: 20px 90px 0 90px !important;
    }

    #sidebar h3,
    #sidebar h3 a,
    #sidebar h5 {
        text-shadow: none;
        color: #ffffff;
    }

    #content,
    .content_section {
        background: #000000;
        box-shadow: none;
        border-color: #808080;
    }

    #content h2,
    #content h3 {
        color: #ffffff;
    }

    .app_icons li .img img {
        display: none;
    }
    .app_icons li a,
    .app_icons li a:focus,
    .app_icons li a:hover {
        color: #ffffff;
    }
    .app_icons li small {
        color: #808080;
    }

    #container footer {
        background: #000000;
        border-bottom: none;
    }


    /* login */
    .forgot-content,
    .signup-content {
        background: #000000;
    }

    .page-login .large-6:first-child {
        display: none;
    }
    .page-login .large-6:last-child {
        width: 100%;
    }

    .coreform-container {
        background: #000000;
    }
    .coreform-container .form-field input {
        background: #000000;
        color: #dddddd;
    }

    .overlay_screen .loginform-container h2,
    .page-login h2 {
        color: #ffffff;
    }

    .coreform-container .forgot-msg,
    .coreform-container .login-error,
    .coreform-container .login-msg {
        background: #000000;
        color: #ffffff;
        border: 1px solid #ffffff;
    }

    .btn-secondary {
        background: #000000;
        text-shadow: none;
        color: #ffffff !important;
    }
    .btn-secondary .text {
        color: #dddddd;
    }

    .coreform-container .forgot,
    .coreform-container .signup {
        color: #ffffff;
    }


    /* options */
    .header-global,
    .footer-global {
        background: #000000;
    }

    .header-global li:first-child a,
    .header-global nav a {
        border-top-color: #808080;
    }

    .legacy-container {
        background: #000000;
    }

    .legacy-sidebar h5,
    .legacy-sidebar h5 > a {
        color: #808080;
    }

    .legacy-content {
        background: #000000;
    }
    .legacy-content h2 {
        color: #ffffff !important;
    }
    .legacy-content input {
        background: #000000;
        color: #dddddd;
    }

    .page-premiumsettings .features-list li,
    .page-account .options_list li {
        background: #000000;
    }

    .legacy-content .msg {
        background: #000000;
    }


    /* pocket hits */
    .pocket-hits #container {
        background: #000000;
    }

    .post_title a {
        color: #808080;
    }


    /* explore */
    .portal-header {
        background: #000000 !important;
    }
    .portal-nav {
        background: #000000 !important;
    }

    .explore_search input {
        background: #000000;
        color: #dddddd;
    }

    .trending_list,
    .best_of_list,
    .topic-heading {
        background: #000000;
    }

    .title a,
    .title_sponsor a {
        color: #808080;
    }

    .portal_list .item {
        background: #000000;
    }
    .portal_list .item article .item_content {
        min-height: 364px;
    }
    .item_image {
        border-bottom: none;
    }
    .item_article {
        border: 1px solid #808080;
    }


    /* list */
    .side-nav {
        background: #000000 !important;
    }

    .popover-new {
        background: #000000 !important;
    }

    .item_content {
        background: #000000 !important;
    }

    .page_queue_grid .item .sub {
        background: #000000 !important;
    }


    /* articles */
    #page .pkt-nav {
        background: #000000 !important;
    }

    .reader_explore_bar {
    	background: #000000;
        z-index: 10000;
    }
}
