css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("moodle.vle.monash.edu") {
  a {
    transition: color 0.25s, text-decoration 0.25s;
  }
  a:hover, a:focus {
    color: lightblue;
  }
  body {
    background-color: #232425;
    color: #b6b6b6;
  }
  button, input.form-submit, input[type="button"], input[type="submit"], input[type="reset"] {
    color: #b6b6b6 !important;;
    text-shadow: none !important;
    background-color: #151f27 !important;
    background-image: -moz-linear-gradient(top, #232425, #151f27) !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#232425), to(#151f27)) !important;
    background-image: -webkit-linear-gradient(top, #232425, #151f27) !important;
    background-image: -o-linear-gradient(top, #232425, #151f27) !important;
    background-image: linear-gradient(to bottom, #232425, #151f27) !important;
    background-repeat: repeat-x;
    border: none !important;
    -webkit-transition: background-position .5s ease-in-out;
    -moz-transition: background-position .5s ease-in-out;
    -o-transition: background-position .5s ease-in-out;
    transition: background-position .5s ease-in-out
  }
  button:hover, input.form-submit:hover, input[type="button"]:hover, input[type="submit"]:hover, input[type="reset"]:hover, button:focus, input.form-submit:focus, input[type="button"]:focus, input[type="submit"]:focus, input[type="reset"]:focus {
    background-position: 0 -15px;
    -webkit-transition: background-position .5s ease-in-out;
    -moz-transition: background-position .5s ease-in-out;
    -o-transition: background-position .5s ease-in-out;
    transition: background-position .5s ease-in-out;
  }
  .block {
    background-color: #151f27;
    border: 1px solid #021d2a;
    border-radius: 2px;
    box-shadow: 0px 2px 2px 0px #00000080;
  }
  .block .header h2, .block .content h3 {
    text-shadow: 0 1px 0 rgba(0,0,0,0.5);
  }
  .block .header h2, .navbar .nav > li > a, .breadcrumb > li > .divider {
    color: #0076b1;
  }
  
  .breadcrumb {
    background-color: #151f27;    
    box-shadow: 0px 1px 2px 0px #00000080;
  }
  .breadcrumb > li {
    text-shadow: 0 0 0 black;
  }
  .breadcrumb > li a:hover {
    color: white;
  }
  
  .calendar_event_course {
    background-color: #602025;
  }
  
  .coursebox {
    background-color: #303030;
    border: 1px solid #021d2a;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
    box-shadow: 0px 1px 2px 0px #00000040;
  }
  
  .course-content .current {
    background-color: #0c2d4a;
  }
  .course-content ul li.section.main {
    border-bottom: 2px solid #3e3e3e;
  }
  .course-content ul.ctopics li.section .content .toggle, .course-content ul.ctopics li.section .content.sectionhidden {
    background-color: #151f27;
    margin-bottom: 6px;
    transition: background-color 0.25s;
    border-radius: 2px;
  }
  .course-content ul.ctopics li.section .content div.toggle:hover {
    background-color: #004384;
  }
  .course-content ul.ctopics li.section .content .toggle span {
    color: inherit;
  }
  .course-content .section-summary {
    border: 1px solid #3e3e3e;
  }
  #dock .dockedtitle {
    background-color: #0c2d4a !important;
    background-image: -moz-linear-gradient(top, #232425, #0c2d4a) !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#232425), to(#0c2d4a)) !important;
    background-image: -webkit-linear-gradient(top, #232425, #0c2d4a) !important;
    background-image: -o-linear-gradient(top, #232425, #0c2d4a) !important;
    background-image: linear-gradient(to bottom, #232425, #0c2d4a) !important;
    background-repeat: repeat-x;
    text-shadow: none !important;
    color: #b6b6b6;
    border-color: transparent;
  }
  #dock .dockedtitle:hover, #dock .dockedtitle:focus {
    color: white;
    text-decoration: none;
    background-position: 0 -15px;
    -webkit-transition: background-position .1s linear, color 0.1s linear;
    -moz-transition: background-position .1s linear, color 0.1s linear;
    -o-transition: background-position .1s linear, color 0.1s linear;
    transition: background-position .1s linear, color 0.1s linear;
  }
  #dockeditempanel .dockeditempanel_content {
    background-color: #151f27;
    width: 384px;
    border: 1px solid #3e3e3e;
    -webkit-box-shadow: 2px 4px 4px 2px #0000001a;
    -moz-box-shadow: 2px 4px 4px 2px #0000001a;
    box-shadow: 2px 4px 4px 2px #0000001a;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
  }
  #dockeditempanel .dockeditempanel_hd {
    border-bottom: 1px solid #3e3e3e;
  }
  #dockeditempanel .dockeditempanel_hd h2 {
    text-shadow: none;
  }
  .nav-tabs {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  }
  .nav-tabs > .active > a, .nav-tabs > .active > a:hover, .nav-tabs > .active > a:focus {
    background-color: #151f2700;
    background-image: -moz-linear-gradient(top, #151f27ff, #151f2700) !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#151f27ff), to(#151f2700)) !important;
    background-image: -webkit-linear-gradient(top, #151f27ff, #151f2700) !important;
    background-image: -o-linear-gradient(top, #151f27ff, #151f2700) !important;
    background-image: linear-gradient(to bottom, #151f27, #151f2700) !important;
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom-color: transparent;
    color: #b6b6b6;
  }
  .navbar-inner {
    
    background-color: #0c2d4a;
    background-image: none;
    
    /*
    background-image: linear-gradient(to bottom, #004384, #0c2d4a);
    background-image: -o-linear-gradient(top, #004384, #0c2d4a);
    background-image: -webkit-linear-gradient(top, #004384, #0c2d4a);
    background-image: -moz-linear-gradient(top, #004384, #0c2d4a);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#004384), to(#0c2d4a));
    */
    border-bottom: 1px solid #021d2a;
  }
  .navbar .nav > li > a {
    text-shadow: none;
  }
  hr {
    margin: 20px 0;
    border: 0;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  }
  header.navbar nav .brand {
    background: url(https://i.imgur.com/OWbLfA0.png) no-repeat;
  }
  header.navbar .container-fluid {
    padding-top: 6px;
  }
  .path-grade-report-user .user-grade.generaltable .levelodd {
    background-color: rgba(255, 255, 255, 0.1);
  }
  #page-footer {
    border-top: 2px solid #3e3e3e;
  }
  
  #page-mod-forum-view .unread, .forumpost.unread .row.header, .path-course-view .unread, span.unread {
    background-color: #3e3e3e
  }
  
  .usermenu .moodle-actionmenu .toggle-display:hover, .navbar .nav > li > a:focus, .navbar .nav > li > a:hover {
    color:white;
  }
  
  img.userpicture.defaultuserpic {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.imgur.com/bfga9Jb.png) no-repeat;
    background-size: cover;
    width: 35px; /* Width of new image */
    height: 35px; /* Height of new image */
    padding-left: 35px; /* Equal to width of new image */
  }
  
  a > img.userpicture.defaultuserpic {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.imgur.com/bfga9Jb.png) no-repeat;
    background-size: cover;
    width: 50px; /* Width of new image */
    height: 50px; /* Height of new image */
    padding-left: 50px; /* Equal to width of new image */
  }
  #toggle-all .content .toggle-arrow-hover h4 span.off {
    transition: background-image 0.25s;
  }
  table#form td.submit, .form-buttons, .path-admin .buttons, #fitem_id_submitbutton, .fp-content-center form + div, div.backup-section + form, #fgroup_id_buttonar {
    background-color: #151f27;
    border-top: 1px solid #021d2a;
  }
  table.flexible th, .generaltable th, table.flexible td, .generaltable td {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
  }
}
