css:
@-moz-document domain("mpek.org.ua") {
body{
    background:#efefef !important;
}
.sidebar h2 {
    background: #f5f5f5;
    border:1px solid #ddd;
    border-radius: 0;
    -moz-border-radius: 0;
    color: #0000;
    font-family: Arial,Helvetica,Sans-serif;
    font-size: 14px;
    font-weight: normal;
    letter-spacing: 0;
    line-height: 16px;
    margin: -5px -5px 5px -5px;
    padding: 8px 8px 6px;
    text-shadow: 0 1px 0 #000000;
    text-transform: none;
}
#contentwrap {
    background: #efefef;
    border-radius: 0 5px 5px 5px;
    -moz-border-radius: 0 5px 5px 5px;
    margin: 0;
    padding: 20px 0 0 0;
    width: 980px;
    overflow: hidden;
}
#nav li{
    margin: 0 !important;
}
#nav a {
    background: #fafafa;
    border-radius: 0;
    -moz-border-radius: 0;
    padding: 11px;
    border:1px solid #ddd;
}
.smOW{
    height:auto !important;
}
#subMenusContainer a, #nav li li a {
    background-color: #efefef;
}
#subMenusContainer a:hover, #subMenusContainer a:focus, #subMenusContainer a.subMenuParentBtnFocused {
    background-color: #fff;
}
#content {
    background: #efefef;
    padding-bottom: 15px;
    margin-right: 7px;
}
#images{
    margin: 0 auto 0 auto;
}
#slider{
    background:#fff;
    padding:5px;
    border:1px solid #ddd;
}
.post {
    background: #fff;
    border-bottom: 1px solid #ddd;
    margin-bottom: 30px;
    padding:8px;
    padding-bottom: 15px;
}
img .aligncenter{
    text-align:left;
}
.edntry img{
    width:50%;
}
.post:hover {
    background: #fff;
    transition: all 0.3s ease;
}
.sidebar{
    background:#efefef;
    border:1px solid #ddd;
}
.poller{
    background:#fff;
}
.sidebar ul li {
    background: #fff;
}
.new_menu li a, .new_link, #nav_menu-2 ul li a {
    border:1px solid #ddd;
    border-radius: 5px;
    background-color: rgba(242,242,242,0.9) !important;
}
#credits{
    visibility:hidden;
}
.outer{
    background:#fafafa;
    border-top:1px solid #ddd;
}
}
