css:
@-moz-document url-prefix("https://www.7cups.com/") {

.dropdown-menu {
      background: #212930;
      color: white !important;
}
  
 .card {
      background: #212930;
      color: white;
}
  


#navbar-main ul.navbar-nav .notification-menu>.dropdown-item {
      color: white !important;
}

  
.dropdown-item {
      color: white;
}
.dropdown-item:focus,
.dropdown-item:hover {
      background: #13181C !important;
      color: white;
}
.text-body {
      color: white !important;
}
.note {
      color: white !important;
}
.note > .text-body {
      color: white !important;
}
/* Nav background */

.gradient-happy,
.navbar-primary-rev.navbar-fixed-top {
      background: rgba(46, 62, 73, 1) !important;
      color: blue;
}
body {
      background: #13181C;
      background-size: cover;
      background-attachment: fixed;
}
.navbar-nav .nav-link
/* nav links */

.navbar-primary-rev ul.navbar-nav>li.nav-item>a.nav-link {
      color: white;
}
.navbar-primary-rev ul.navbar-nav .compassionJar {
      background: rgba(46, 62, 73, 0.1) !important;
}
.alertCounter {
      background-color: #606c75 !important;
}
#bottomStatus.heavyLoad {
      background: #2e3e49 !important;
}
/* gr queue */

#bottomStatus .btn-primary {
  	  background: rgba(46, 62, 73,  0);
      border: 0px;
}
#bottomStatus {
      background: rgba(46, 62, 73, 1) !important;
}
.fa-check {
      color: #2e3e49;
}
.feedFollowers {
      color: #2e3e49;
}
.badge {
      background: #2e3e49;
}
.btn-primary {
      background: #2e3e49;
      border: none;
}
.btn-primary:hover {
      background: #606c75;
}
.btn-info:hover {
      background: #606c75;
}
.btn-info {
      background: #2e3e49;
      border: none;
}
.btn-simple.btn-warning {
      color: white;
      background: #606c75;
      border: none;
}
.btn-simple.btn-warning:hover {
      background: #2e3e49;
      border: none;
}
.btn-simple.btn-primary {
      border: none;
      color: white;
}
.nav-tabs.chat-tabs .nav-link.active {
      border-bottom: solid .0625rem #2e3e49;
      color: #2e3e49;
      background-color: rgba(23, 35, 40, 0.1);
}
.nav-tabs.chat-tabs .nav-link {
      color: #2e3e49;
}
.chat-list a.list-group-item.activeConv,
.chat-list a.list-group-item.activeConv:hover {
      background-color: rgba(23, 35, 40, 0.1);
}
.chatBox .meWrap {
      background-color: #2e3e49;
      border-bottom-left-radius: 1em;
      border-top-left-radius: 1em;
      border-top-right-radius: 1em;
}
.chatBox .youWrap {
      background-color: #606c75;
      color: white;
      border-bottom-left-radius: 0em;
      border-top-left-radius: 1em;
      border-top-right-radius: 1em;
      border-bottom-right-radius: 1em;
}
.chatBox .details {
      color: #606c75;
}
.chatBox .card-title {
      color: #606c75;
}
#sendButton {
      background-color: #2e3e49;
}
#bottomStatus .btn-default {
      background: linear-gradient( #e74c3c, #c0392b);
      border: 0px;
      color: white;
}
.fa-group:before,
.fa-users:before {
      color: #606c75;
}
.fa-comments:before {
      color: #606c75;
}
.youWrap a {
      color: white;
      text-decoration: underline;
}
.chatRow.expanded {
      background: #2e3e49;
}
.card {
      border-radius: 0px;
}
.related-community-holder {
      display: none;
}
.fa-arrow-up {
      Color: #2e3e49;
}
.compassionHeart {
      color: #606c75;
}
.fa-heart {
      color: #606c75;
}
.gradient-footer,
footer {
      background: #2e3e49;
}
.chatBox .card-block #MessageScrollDiv {
      background: #212930;
}
.chatBox .card-title {
      background: #313d47;
      margin-bottom: 0px;
}
#actionButtons {
      background: #212930;
      border-radius: 5px;
}
.chatBox .chatInput #Comment {
      background: #313d47;
      border-radius: 0px;
      margin-top: 0px;
      color: white;
}
.chatroomTopic {
      background: #313d47;
}
.conversationBox {
      background: #212930;
}
.card>.list-group:first-child .list-group-item {
      background: #2E3E49;
}
.threadTitle {
      color: #9b9fa9;
}
.card-block {
      background: #212930;
}
.btn-simple {
      color: black;
}
.data {
      color: white !important;
}
.reposterList {
      color: white;
      /*list-style-image:;*/
}
.chatroomTopic div {
      color: black !important;
}
.quote-close-big {
      background-image: url(https://d30y9cdsu7xlg0.cloudfront.net/png/19278-200.png);
      background-size: 30px 30px;
}
.quote-open-big {
      background-image: url(https://d30y9cdsu7xlg0.cloudfront.net/png/19279-200.png);
      background-size: 30px 30px;
}
.chat-expand,
.bio-expand {
      background-image: url(https://i.imgbox.com/cV2TEfdx.png);
      background-size: 30px 30px;
}
.chat-expand.open,
.bio-expand.open {
      background-image: url(https://i.imgbox.com/hkcGJ9XT.png);
      background-size: 30px 30px;
}
.navbar-primary-rev .notification-menu>.dropdown-item {
      background: #212930;
      color: white;
}
.navbar-primary-rev .notification-menu>.dropdown-item+.dropdown-item {
      border-top: #313d47 1px solid;
}
.navbar-primary-rev ul.navbar-nav.pull-right>li.nav-item>.dropdown-menu,
.navbar-primary-rev ul.navbar-nav.pull-right-xs-up>li.nav-item>.dropdown-menu {
      padding: 0px;
}
.chat-list a.list-group-item.newMsg,
.chat-list a.list-group-item.newMsg:hover {
      background: #313d47;
}
a.list-group-item,
button.list-group-item {
      color: white;
}
.modal-body {
      background: #212930
}
.modal-header {
      background: #313d47;
}
.modal h3 {
      color: #212930;
}
.modal h5 {
      color: #313d47;
}
.card-secondary {
      background: #212930 !important;
}
.navbar-tabs.bg-yellow {
      background: #212930;
}
.navbar-tabs.bg-yellow a {
      color: white !important;
}
.community-about {
      color: white;
}
.gradient-happy,
#navbar-main {
      background: #212930;
}
#navbar-main ul.navbar-nav>li.nav-item .compassionJar {
      background: rgba(0, 0, 0, 0.1);
}
.navbar-tabs.bg-yellow .navbar-nav>.nav-item>.nav-link.active:after {
      color: #313d47;
}
.userName {
      color: #9b9fa9;
}
.list-group-item {
      background: #2E3E49;
}
.breadcrumb {
      background: #212930;
}
.card-title {
      color: #9b9fa9;
}
.upcomingListItem__what {
      color: #9b9fa9 !important;
}
.upcomingListItem__when {
      color: #9b9fa9 !important;
}
#chatStats > div > div {
      background: #212930;
}
a.list-group-item:focus,
a.list-group-item:hover,
button.list-group-item:focus,
button.list-group-item:hover {
      background-color: inherit;
}
.table-hover tbody tr:hover {
      background-color: inherit;
}
.modal-footer {
      background: #313D47 !important;
}
.modal-footer > .btn-primary {
      background: #212930 !important;
}
.modal-body > .convHolder {
      color: blue !important;
}
#listenersInChatroomModal > h3 {
      color: blue !important;
      text-decoration: none;
}
#listenersInChatroomModal > .modal-dialog > .modal-content > .modal-body {
      background: #313D47 !important;
} }

@-moz-document url-prefix("https://www.7cups.com/forum/") {
  .fa-weixin {
      color: #2e3e49 !important;
}
.fa-arrow-up {
      color: #2e3e49 !important;
}
.fa-clock-o {
      color: #2e3e49 !important;
}
.pagination>.active>a,
.pagination>.active>a:focus,
.pagination>.active>a:hover,
.pagination>.active>span,
.pagination>.active>span:focus,
.pagination>.active>span:hover {
      background-color: #2e3e49;
      border: none;
}}
