css:
@-moz-document domain("dynalist.io") {


.DocumentContainer, .AppContainer.is-borderShowing.is-globalSearch .DocumentContainer{
    background: unset;
}

header.AppHeader{
    height: 48px!important;
    box-sizing: padding-box;
    box-shadow: none!important;
    border-bottom: 1px solid #ddd!important;
    background-color: unset!important;
}

.AppHeader-desktopControls{
    display: flex;
}

.AppHeader-settings, .AppHeader-notifications, .AppHeader-mainMenu{
    box-sizing: border-box;
    height: 47px;
    width: 48px;
    top: 0px;
    text-align: center;
    line-height: 40px;
    border-bottom: 1px solid #ddd;
    bottom: unset;
    border-radius: 0;
    border: none;
}

.AppHeader-mainMenu.active, .AppHeader-notifications.active, .AppHeader-settings.active{
 	box-shadow: none;
    background-color: #f6f6f6;
    border: 1px solid #ddd;
    top: -1px;
}

.AppHeader-mainMenu.active:hover, .AppHeader-notifications.active:hover, .AppHeader-settings.active:hover{
 	border: 1px solid #ddd;   
}

.AppHeader-mainMenu:hover, .AppHeader-notifications:hover, .AppHeader-settings:hover{
 	box-shadow: none!important;
    border-radius: 0;
    background-color: #f6f6f6;
    border: none;
}

.AppHeader-mainMenu{
    right: 0;
}

.AppHeader-settings{
    right: 48px;
}

.AppHeader-notifications{
    right: 96px;
}

.AppHeader-syncStatus{
 	line-height: 48px;
    top: -4px;
    padding-right: 8px;
    right: 144px;
}

.NotificationsContainer, .main-menu{
    top: 56px;
}

.NotificationsContainer{
    right: 72px;
}

.LeftPaneSlidebarContainer{
    box-shadow: none!important;
    border-right: 1px solid #ddd!important;
    top: -1px;
}

.LeftPaneContainer-nav{
    top: -1px;
    padding-top: 0!important;
    width: 48px!important;
    background-color: #fff!important;
    border-right: 1px solid #ddd!important;
}

.LeftPaneContainer-panes{
    width: calc(100% - 48px)!important;
}

.LeftPaneContainer-navItem{
    box-sizing: border-box!important;
    padding-top: 12px!important;
    width: 48px!important;
    height: 48px!important;
    line-height: 48px;
    background-color: unset!important;
    float: unset!important;
}

.LeftPAneContainer-navItem:hover{
 	background-color: #ddd!important;   
}

.LeftPaneContainer-nav .is-active{
    border: 1px solid #ddd!important;
    position: relative;
    top: -1px;
}

.LeftPaneContainer-navItem--myFiles.is-active{
 	top: 0!important;
    padding-top: 11px!important;
}

.LeftPaneContainer-navItem:after{
    font-size: 20px!important;
}

.Pane-header{
 	height: 48px!important;
    display: flex;
    flex-direction: row;
    align-content: center;
    background-color: unset!important;
    border-bottom: 1px solid #ddd!important;
}

.Pane-headerText{
    line-height: 48px;
    font-size: 16px;
}

.Pane-headerToolbar{
    height: unset;
    position: unset;
    bottom: unset;
    right: unset;
    margin-left: auto;
    display: inline-flex;
    flex-direction: row;
    align-content: center;
    align-items: center;
}

.Pane-headerToolbarItem{
    text-align: center;
    width: 48px;
    box-sizing: border-box;
}

.Pane-content{
    padding: 0;
}

.DocumentItem, .BookmarkItem, .FolderItem-header, .tag-pane-doc-tag-instance, .tag-pane-global-tag-instance, .tag-pane-global-tags-header, .tag-pane-doc-tags-header{
 	height: 36px!important;
    margin: 0!important;
}

.tag-pane-global-tags-header, .tag-pane-doc-tags-header{
 	line-height: 36px;
    padding-left: 12px;
}

.DocumentItem-header, .FolderItem-header, .BookmarkItem-header, .tag-pane-doc-tag-instance, .tag-pane-global-tag-instance{
    padding: 6px 12px;
	width: 100%;
    height: 100%;
}

.FolderItem .DocumentItem, .tag-pane-doc-tag-instance, .tag-pane-global-tag-instance{
    padding-left: 32px;
}

.BookmarkItem{
    position: relative;
    left: -3px;
}

.FileFinder-search{
    height: 48px;
    border-radius: 0;
    border: 1px solid rgba(221, 221, 221, 0.5);
}

.FileFinder-results{
    top: 108px;
}

.tag-pane-global-tags-container, .tag-pane-doc-tags-container{
    margin: 0;
}
  
  .DocumentContainer.is-centerDocument .Document, .DocumentContainer.is-centerDocument .DocumentBreadcrumb{
    max-width: 1200px;
  }
}
