@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pathofexile.com")
    
{
    
    
/*forumbg*/
    
body
{
background: #C9D2B3 !important;
}
    
/*font*/
  
body, a, .forumTable, strong, element, div, em, .colourDefault, h2
{
color: #000000 !important;
text-shadow: none !important;
}
    
*[href*="http"]
{
color: #264D00 !important;
}
    
/*konu baslik ve konu ici mesajlar*/
    
tr
{
background: #C3C3C3 !important;
}
  
tr.even
{
background: #b3b3b3 !important;
}
    
tr.staff
{
background-image: -moz-linear-gradient(top, transparent 80%, #FF0808 100%) !important;
border: 1px solid #FF0808 !important;
outline: 1px solid #FF0808 !important;
}
  
.layoutBoxTitle
{
color: #000000 !important;
text-shadow: none !important;
}
  
.post_info 
{
border: 1px solid #000000 !important;
}

  
/*spoiler*/
  
.spoilerTitle, .spoilerContent
{
background: #9E9E9E !important;
}
  
/*quote*/
  
blockquote
{
background: #f2f2f2 !important;
}

/*siyah alan duzenlemesi*/
  
.topBar, .botBar, .forumSortContainer, .sitemap, .bottom
{
background: #9E9E9E !important;
border: 1px solid #737373 !important;
box-shadow: none !important;
}
  
.pagination a, .forum_pagination a, .button1, .links .selection:last-child a, .verifiedStatus
{
background-color: #f2f2f2 !important;
}
  
div#statusBar, .statusBarCenter, .statusBarRight
{
background: none !important;
}
  
/*supporter & challenge*/
  
.achievement, .clearfix
{
display: none !important;
}
  
/*itemhover*/
  
.newItemPopup, .itemPopupContainer, .itemHeader
{
background: #C3C3C3 none !important;
color: #000000 !important;
border: 2px solid #000000 !important;
}
  
/*text input*/
.textInput, textarea
{    
background: #9E9E9E !important;
border: 1px solid #737373 !important;
box-shadow: none !important;
color: #000000 !important;
}


}
