css:
@-moz-document domain("sicuaplus.uniandes.edu.co") {
a:link {color: #128fa8 !important;}
a:visited {color: #0d566d !important;}
a:hover {color: #000000 !important;}
a:active {color: #000000 !important;}


header {
    background-color: #007077;
    height: 90px;
}
body {
    font-family: Gotham, "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 12px;
    background-color: #4c4c4c !important;
    margin: 0;
    padding: 0;
    color: #000;
    background-repeat: repeat-x;
    background-position: 0 90px;
}

.container {
    background-color: #828080;
    color: #FFFFFF;
}

#contentFrame, #schemePreview, body, .ineditmode, .topFrame, .breadcrumbs, .paneTabs, .paneTabs ul li.active, .paneTabs ul li, .wizardBody .containerWizard {
    background: url(https://sicuaplus.uniandes.edu.co/branding/themes/Uniandes5/images/bblearn_2011_bg.png) repeat 0 0;
    background-color: #11708e;
    color: #000000;
}

#topFrame, .bgBanner {
    background-color: #11708e;
    border-color: #909090;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}

.portal .contentBox {
    background: rgb(185, 185, 185);
    -moz-box-shadow: 0 1px 4px rgba(0,0,0,0.15);
    -webkit-box-shadow: 0 1px 4px rgba(0,0,0,0.15);
    box-shadow: 0 1px 4px rgba(0,0,0,0.15);
}


#pageHeader, #schemePreviewHead, div.pageTitle, div.pageTitle h1, div.pageTitleError h1, div#lightboxContent h2 {
    background: #828080;
    border-color: #ffffff;
    color: #000000;
    text-shadow: none;
}

.localViewToggle ul {
    -moz-border-radius: 3px 3px 0 0;
    -webkit-border-radius: 3px 3px 0 0;
    background: #828080;
    -moz-box-shadow: 1px 0 0 #fafafa inset;
    -webkit-box-shadow: 0 0 0 #828080 inset;
    margin: 0;
    padding: 0 0 4px 0;
}

div.localViewToggle+div.pageTitle {
    padding-top: 0;
    -moz-box-shadow: 1px 0 0 #fafafa inset;
    -moz-border-radius: 0;
    -webkit-box-shadow: 1px 0 0 #fafafa inset;
    -webkit-border-radius: 0;
    box-shadow: 1px 0 0 #828080 inset;
    border-radius: 0;
}

#pageHeader, #schemePreviewHead, .pageTitle {
    padding: 20px 30px;
    margin: 0;
    background: -moz-linear-gradient(100% 100% 90deg,#dedede,#eee);
    background: -webkit-gradient(linear,0 0,0 100%,from(#eee),to(#dedede));
    -ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr=#eeeeee, endColorstr=#dedede";
    -moz-border-radius: 3px 3px 0 0;
    -webkit-border-radius: 4px 4px 0 0;
    -moz-box-shadow: 1px 1px 0 #fafafa inset;
    -webkit-box-shadow: 1px 1px 0 #828080 inset;
}

#actionBarBrand, .actionBar, .contentPaneWide .actionBar, div.actionBar ul, div.localViewToggle, .actionBarMicro {
    background-color: rgba(202, 0, 0, 0);
    background: rgb(18, 108, 136);
    border-color: transparent;
}
}
