css:
@-moz-document domain(www.goatlings.com) {
/* closet edit */

  .base_area
  {
    float: left;
    width: 130px;
    margin-top: 42px;
  }

  #item_area_ajax
  {
    float: right;
    width: 585px !important;
    height: 500px !important;
    overflow: scroll;
  }

  .item_area
  {
    width: 582px !important;
    float: right !important;
  }

  .categories
  {
    margin-bottom: 5px;
  }

  .closetItem
  {
    border-radius: 5px;
    padding: 0;
    margin: 1px;
  }

 /* Image Fixes, sourced mostly from "Dark Goatlings Theme */ 
  img[src*="DD2"]
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 20px !important;
    padding-top: 20px !important;
    background: url("https://68.media.tumblr.com/51400954ed3ba1b92709a3034aa157e9/tumblr_inline_oo5upy9WXV1ugpadk_500.gif") no-repeat !important;
    pointer-events: none;
  }    
  
  .profile-part-two img[src*="Neutral.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 20px;
    padding-top: 20px;
    background: url("https://68.media.tumblr.com/d65f293ce4525a662c0b7cfcf8ecd60d/tumblr_inline_op6vo5Nftc1ugpadk_540.gif") no-repeat !important;
  }

  .profile-part-two img[src*="Other.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 20px;
    padding-top: 20px;
    background: url("https://68.media.tumblr.com/75c98ace70b4538181232fd51805dc9b/tumblr_inline_op6vo5ghgQ1ugpadk_540.gif") no-repeat !important;
  }

  .profile-part-two img[src*="undecided.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 20px;
    padding-top: 20px;
  }
  .mystuff img[src*="blank.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 140px;
    padding-top: 140px;
  }
    #content img[src*="westpt.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 52px;
    padding-top: 629px;
  }

  #content img[src*="eastpt.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 52px;
    padding-top: 629px;
  }

  #content img[src*="southpt.gif"]
  {
    width: 0px;
    height: 0px;
    padding-left: 629px;
    padding-top: 52px;
  }


/* style edits */ 
 
  body
  {
    background-image: url(http://i.imgur.com/Zjfj4zv.png);
    background-repeat: no-repeat;
    background-position: top;
    background-attachment: fixed;
    background-size: cover;
    color: rgb(51, 51, 51);
  }

  #header
  {
    height: 0 !important;
    width: 0 !important;
    padding-left: 940px !important;
    padding-top: 125px !important;
    background: url(http://i.imgur.com/JBxjekQ.png) no-repeat !important;
    pointer-events: none;
  }

  #wrapper
  {
    background-color: rgb(66, 66, 66);
  }

  #content
  {
    background-color: #efefef;
    border-radius: 5px 5px 5px 5px;
    color: rgb(51, 51, 51) !important;
  }

  #user-info-wrap
  {
    background-color: rgba(51, 51, 51, 0.75);
    border-radius: 5px 5px 5px 5px;
    padding: 5px;
    margin-top: 5px;
    margin-left: 5px;
    margin-right: 13px;
    color: #7c817a;
  }

  .small-text
  {
    color: rgb(189, 189, 189);
  }

  .navlink img
  {
    filter: grayscale(100%);
  }

  .search-top img, .search-mid, .search-bot img
  {
    filter: grayscale(100%);
  }

  .page p
  {
    color: #4c4c4c;
  }

  a
  {
    text-decoration: none;
    color: #a35656;
    font-weight: bold;
    -moz-transition-duration: 0.3s;
    -webkit-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
  }

  a:hover
  {
    color: #ED8080;
    transition: color .25s ease-in-out;
    -moz-transition-duration: 0.3s;
    -webkit-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
  }

  .button
  {
    border: none !important;
    background: #8c8c8c;
    padding: 4px;
    color: #FFF;
    border-radius: 4px 4px 4px 4px;
    cursor: pointer;
    -moz-transition-duration: 0.3s;
    -webkit-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
  }

  .button:hover
  {
    background: #424242;
    color: #FFF;
    border: none !important;
    transition: background .25s ease-in-out;
    -moz-transition-duration: 0.3s;
    -webkit-transition-duration: 0.3s;
    -o-transition-duration: 0.3s;
  }

  .closetItem
  {
    width: 100px;
    border-radius: 5px;
    padding: 5px;
  }

  .item_area
  {
    border: 2px solid #424242;
  }

  .base_area
  {
    border: 2px solid #424242;
  }

  .item-invent
  {
    border: 1px solid #b6b6b6;
    padding: 3px;
    border-radius: 5px;
  }

  .itema
  {
    border: 1px solid #b6b6b6;
    padding: 3px;
    border-radius: 5px;
  }

  .news-post
  {
    border: 1px solid #b6b6b6;
  }

  .item-invent-view
  {
    border: 1px solid #b6b6b6;
  }

  .page-title
  {
    border-bottom: 1px solid #b6b6b6;
    background: none !important;
  }

  .flash_data
  {
    border: 1px solid #b6b6b6;
  }

  .random_event
  {
    border: 1px solid #b6b6b6;
  }

  .event
  {
    border: 1px solid #b6b6b6;
  }

  .mypets-pet
  {
    border: 1px solid #b6b6b6;
  }

  .profile-info
  {
    border: 1px solid #b6b6b6;
    padding-left: 15px;
    padding-right: 15px;
  }

  .profile-part-two
  {
    text-align: center;
  }

  p
  {
    color: rgb(51, 51, 51);
  }
  
    #m, #n {
    border: 1px solid rgb(66, 66, 66) !important;
    background-color: #efefef;
  }

  #n
  {
    padding-bottom: 15px !important;
  }

  h1
  {
    background-color: rgb(66, 66, 66) !important;
    border-bottom: 10px solid rgb(66, 66, 66) !important;
  }

  h2, h3
  {
    background-color: rgb(66, 66, 66)  !important;
  }

/* Item Sorter Edit */

@-moz-document url("http://www.goatlings.com/itemsorter") {
.table-header
  {
    background-color: rgb(66, 66, 66);
    color: rgb(189, 189, 189);
  }

  tr
  {
    border: 1px solid rgb(66, 66, 66) !important;
    background-color: #b6b6b6;
  }
}

/* Forum Edits*/ 
@-moz-document url-prefix("http://www.goatlings.com/forums/") {
tbody
  {
    color: rgb(66, 66, 66);
  }

  tr
  {
    background-color: #cccccc;
  }
  }
  }
