css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

:root
{
  --ui-basecolor: #585858;
  --ui-background: #333333;
  --ui-darkcolor: linear-gradient(#585858, #333333) repeat-x !important;
  --ui-accent: #333333;
  --tab-selection-background-color: var(--ui-basecolor) !important;
  --tab-hover-background-color: #45545E !important;
  --chrome-background-color: var(--ui-darkcolor) !important;
 /*includes tabstrip background*/
  --chrome-secondary-background-color: var(--ui-basecolor) !important;
 /*includes toolbar, urlbar*/
  --url-and-searchbar-background-color: #1c1c1c !important;
  --chrome-selection-background-color: var(--ui-accent) !important;
  --chrome-nav-bar-controls-border-color: rgba(255, 255, 255, 0) !important;
 /*urlbar border*/
  ;
}

toolbar, nav-bar > *
{
  background: linear-gradient(#585858, #333333) repeat-x !important;
}

.tabbrowser-tab:hover .tab-content,
toolbarbutton:hover
{
  filter: grayscale(0) !important;
}

.tabbrowser-tab[selected="true"] .tab-content
{
  filter: grayscale(0) !important;
}

findbar
{
  background: linear-gradient(#585858, #333333) repeat-x !important;
}

.findbar-button
{
  background: linear-gradient(#585858, #333333) repeat-x !important;
  box-shadow: none;
  color: #eeeeee !important;
  text-shadow: 0 1px rgba(0, 0, 0, .4) !important;
}

textbox
{
  background: #1c1c1c !important;
  color: #eeeeee !important;
  border: none !important;
  box-shadow: none !important;
}

.autocomplete-richlistbox
{
  background: #333333 !important;
  color: #eeeeee !important;
  border-color: transparent !important;
}

toolbarbutton.findbar-find-previous
{
  background: linear-gradient(#585858, #333333) repeat-x !important;
  box-shadow: none;
}

toolbarbutton.findbar-find-next
{
  background: linear-gradient(#585858, #333333) repeat-x !important;
  box-shadow: none;
}

#TabsToolbar .tabbrowser-tab .tab-line
{
  visibility: hidden;
}

#TabsToolbar .tabbrowser-tab
{
  font-size: 13px !important;
}

#urlbar-container
{
  margin-left: 11px !important;
  font-weight: 300;
}

#urlbar[focused="true"],
  .searchbar-textbox[focused="true"]
{
  border: 1px solid #888888 !important;
  box-shadow: none !important;
}

  /* AUTOCOMPLETE */

	/*Default background & color*/
#PopupAutoCompleteRichResult
{
  background: #282828 !important;
}

.autocomplete-richlistitem
{
  border-radius: 0 0px !important;
}
	/*Selected item*/
.autocomplete-richlistitem:hover, .autocomplete-richlistitem[selected]
{
  background: #383838 !important;
  border-radius: 0 0px !important;
  color: #000 !important;
}
	/*Title of autocomplete*/
.ac-title
{
  font-size: 13px;
  font-weight: 400;
  color: #79acf4 !important;
}

.ac-title[selected]
{
  font-size: 13px;
  font-weight: 400;
  color: #fff !important;
}
	/*URL Color*/
.ac-url-text
{
  color: #616161 !important;
}

.ac-url-text[selected]
{
  color: #616161 !important;
}

.ac-action
{
  color: #999999 !important;
}
	/*From bookmark item*/
.autocomplete-richlistitem[type="bookmark"]
{
  font-weight: bold !important;
  background: var(--ui-darkcolor) !important;
}
	/*Found text what you typed*/
.ac-emphasize-text
{
  border-bottom: 1px solid;
  font-weight: 600 !important;
}
