css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("indiegogo.com") {

.siteHeader-content, .siteHeader .exploreMenu, 
.modal-content
{
background-color:#000088 !important;
}
campaign-page, .campaignRelatedContent, 
.layoutFooter, .modalUserAuth-content
{
background-color:#000055 !important;
}
.campaignTags-list a, .discoverableCard,
.campaignNextPerk-cta, .i-cta-1,
.layoutHeader-generalHeader-content,
input
{
background:#EB1478 !important;
}

.layoutHeader-generalHeader-logo
{
background-color: #000055 !important;
}
.layoutHeader-generalHeader-content a:hover
{
color:#000055 !important;
}
a:hover, .modalUserAuth-logIn-headers,
.t-transform--default
{
color:#EB1478 !important;
}
body, campaign-page, a, campaign-next-perk,
.campaignHeaderBasics-tagline, .campaignGoalProgress,
.campaignGoalProgress-raisedAmount, .ng-binding,
.campaignBody, .ng-scope, .ng-isolate-scope,
input
{
color:#FFFFFF !important;
}
}
