css:
@-moz-document domain("ylc.entry.edu.tw") {
#Itself_header,#topmenu1 ul ,#topmenu1 li,#topmenu1 li a,#Itself_nav,#Itself_nav li:hover > a,#breadcrumb ul li,#Itself_nav ul,.botton01,.datetable th{
	background-image: none !important;
}
.datetable th {
    color: #666666;
}
#Itself_logo,#ctl00_ctl00_ContentPlaceHolder1_imgHOME,#footer,#MARQUEE1,table[style="border: 1px solid #EBEBEB; background: #ffffff; background: -webkit-gradient(linear, 0 0, 0 bottom, from(#ffffff), to(#EBEBEB)); background: -webkit-linear-gradient(#ffffff, #EBEBEB); background: -moz-linear-gradient(#ffffff, #EBEBEB); background: -ms-linear-gradient(#ffffff, #EBEBEB); background: -o-linear-gradient(#ffffff, #EBEBEB); background: linear-gradient(#ffffff, #EBEBEB); -pie-background: linear-gradient(#ffffff, #EBEBEB);"],img[src="../../../../images/login1.png"],#ctl00_ctl00_ContentPlaceHolder1_Label_StudentSpecialMsg,img[src="../../../../images/login-pic.png"],#breadcrumb,#ctl00_ctl00_ContentPlaceHolder1_ContentPlaceHolder1_UCPager1_panPager,#ctl00_ctl00_ContentPlaceHolder1_ContentPlaceHolder1_pnQUERY{
	display:none
}
#Itself_top {
    height: 11px;
}
#ctl00_ctl00_ContentPlaceHolder1_Panel_BNTitleName>div{
    top: 0 !important;
    left: 0 !important;
	font-size: 26px !important;
	width: 100% !important;
	color: #666 !important;
    text-align: center;
    background-color: #fff;
	margin-top: -25px;
}
#Itself_nav{
	background-color: #f2f2f2;
	width: 100%;
}
#Itself_nav a {
    color: #222  !important;
}
#Itself_nav li:hover > a{
	background-color: rgba(0, 0, 0, 0.05);
}
.心測字型,*,body {
    font-family: 'Raleway',-apple-system, "Source Han Sans TC","Helvetica Neue", Helvetica, "Nimbus Sans L", Arial, "Liberation Sans", "PingFang SC", "Hiragino Sans GB", "Source Han Sans CN","Source Han Sans SC", "Microsoft YaHei", "Wenquanyi Micro Hei", "WenQuanYi Zen Hei", "ST Heiti", SimHei, "WenQuanYi Zen Hei Sharp", sans-serif;
    font-weight: 400;
}
#content {
	margin-top: -11px;
}
#ctl00_ctl00_ContentPlaceHolder1_ContentPlaceHolder1_Label_KindName{
	color: #393939 !important;
	line-height: 60px;
}
#Itself_nav ul a{
	text-shadow: 0 1px 0 rgba(255, 255, 255, 0);
	background: #f2f2f2 !important;
}
#Itself_nav ul{
    background: #f2f2f2 ;
}
#Itself_nav ul,#content,#Itself_nav ul li:last-child > a,#Itself_nav ul,#Itself_nav ul li:first-child > a,.botton01 {
	border-radius: 0px !important;
}
#Itself_nav ul a:hover {
    background: #e6e6e6 !important;
	color: #222  !important;
	text-shadow: 0 1px 1px rgba(0, 0, 0, 0);
}
.botton01 {
    color: #222;
}
#ctl00_ctl00_ContentPlaceHolder1_UlWebsite_LeftBanner:before{
	content:'沒有，對，我不知道他幹嘛放'
}
span{
	color:#222 !important;
	font-size:13px !important;
}
body {
    color: #222;
    font-size: 13px;
    background-color: #ffffff;
}
.badge.ng-binding{
	background-color: #e2e2e2 !important;
}
/* 卷軸樣式 */
::-webkit-scrollbar-track
{
	background-color: #F5F5F5;
}

::-webkit-scrollbar
{
	width: 5px;
	background-color: #F5F5F5;
}

::-webkit-scrollbar-thumb
{
	box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #555;
}
}
