css:
@-moz-document domain("qzone.qq.com") {
/*平滑滚动*/
 html {
    scroll-behavior: smooth;
}
/* 删除一些没什么用的东西 */
 #tb_app_li, #tb_dress_li, #tb_music_li, #tb_vip_li, #tab_menu_mv, #tab_menu_class, #tab_menu_app, #tab_switch, #sidebar-ic-fixed, .gb-operation-area .report, .fn_accessLog_tips, .top-fix-bar .user-name.textoverflow, .message-num, [data-feed=视频推荐], [data-feed=好友热播], .head-detail-info, .hot-msg, .qz-level-flag, .qz-app-flag, #view_like_list, .mod-side-nav-recently-used, #speed_signal_btn, #QM_Container_100002 {
    display: none!important;
}
/*card*/
 .profile-left, .image-wall .inner, .f-single, .feed .check-more, .head-avatar, .update-more, .mod-side-nav-message, .col-main-sidebar, .col-main-feed > #QM_Mood_Poster_Container {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2)!important;
    border-radius: 3px!important;
    border: none!important;
}
.col-main-feed .qz-poster {
    margin-bottom: 0;
}
/*input*/
 #QM_Mood_Poster_Container {
    overflow: hidden;
}
.qz-inputer {
    padding: 0 100px 0 0;
}
.qz-poster .qz-inputer div.textarea, .qz-poster .qz-inputer textarea {
    box-shadow: none;
    padding: 4.5px 8px;
}
/*grid*/
 #pageContent {
    width: 1070px!important;
}
#main_feed_container {
    width: 900px!important;
}
.layout-page .col-main .col-main-feed {
    padding-left: 15px;
}
/*background*/
 html, .background-container {
    background: #EEE;
}
/*header*/
 .layout-head .head-info {
    top: 80px!important;
}
.layout-head .head-detail {
    left: 164px!important;
}
.top-fix-inner {
    height: 64px!important;
    display: flex;
    align-items: center;
    box-shadow: 0 1px 8px rgba(0, 0, 0, .3)!important;
}
.top-fix-container {
    width: 100%!important;
    height: auto!important;
    padding: 0 16px;
    box-sizing: border-box;
}
.top-fix-wrap {
    display: flex;
    width: 100%!important;
    align-items: center;
}
.top-fix-inner .user-info {
    position: absolute;
    right: 0;
}
.top-search {
    flex: 1;
    padding: 0;
    margin: 0 128px 0 32px!important;
    max-width: 682px!important;
}
.search-input {
    flex: 1;
    max-width: 666px!important;
    height: 48px!important;
    padding: 0 16px!important;
    box-sizing: border-box!important;
}
.search-box {
    display: flex;
    justify-content: center;
    align-items: center;
}
.search-button {
    display: block!important;
    position: relative!important;
    top: 0!important;
    margin-left: 8px;
}
/*nav*/
 .head-avatar {
    width: 120px!important;
    height: 120px!important;
    border: none!important;
    border-radius: 50%!important;
    overflow: hidden!important;
}
.head-dress {
    width: 120px!important;
    height: 120px!important;
}
.user-avatar {
    border: none!important;
    border-radius: 50%!important;
}
/*left*/
 .profile-left {
    padding: 0;
    background: #FFF;
    width: 335px;
    overflow: hidden;
}
.mod-profile .inner, .mod-profile-headline .inner, .image-wall .inner, .icenter-right-mod {
    padding: 8px 16px;
    border-radius: 0;
    border: 0;
}
.icenter-right-mod h3, .mod-profile h3 {
    margin-left: 0px!important;
    font-size: 24px!important;
    font-weight: 500!important;
}
.icenter-right-mod .hd, .icenter-right-mod .box_hd, .mod-profile .hd {
    height: 36px;
    line-height: 36px;
    padding: 16px 0 8px!important;
    padding: 0;
    background-color: transparent!important;
    border: 0;
}
.mod-profile .bd {
    padding: 0;
}
.visitor-name-bg {
    background-image: none!important;
    text-align: center;
    text-shadow: 0px 1px 3px rgba(0, 0, 0, 0.7);
}
.visitor-avatar-list .close {
    border-radius: 50%!important;
    padding: 4px;
    font-size: 14px;
    box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.14), 0 1px 18px 0 rgba(0, 0, 0, 0.12), 0 3px 5px -1px rgba(0, 0, 0, 0.4);
}
/*right*/
 .layout-page .col-main .col-main-sidebar {
    width: 278px;
    overflow: hidden;
}
.icenter-right-mod {
    margin-bottom: 0;
}
.mod-profile .inner, .mod-profile-headline .inner, .image-wall .inner, .icenter-right-mod {
    padding: 8px 24px;
}
.fn-checkin-btn {
    width: 100%;
    margin-bottom: 0;
    border-width: 0 0 1px 0;
}
/*photo wall*/
 .imgwall-item {
    float: none;
}
.clearfix:after, .qz-grid:after, .qz-grid-visible:after {
    clear: none;
}
.imgwall-item img {
    width: 100%;
}
/*main*/
 .col-main-feed > #QM_Mood_Poster_Container .qz-poster .qz-inputer {
    border-width: 0 0 1px 0!important;
}
.col-main-feed > #QM_Mood_Poster_Container .qz-poster-ft {
    border-width: 0!important;
}
.image-wall .inner, .feed .check-more {
    margin-left: 16px!important;
}
.ui-icon.sp-iw-qmark {
    display: none;
}
.profile-main {
    width: 582px;
    padding-left: 338px;
    overflow: visible;
}
.image-wall .inner {
    margin: 0 8px;
}
#feed_friend > .feed-v9 {
    text-align: center!important;
}
.fn-feed-control-v2, .fn-feed-control-v2 .item-on {
    background-color: transparent!important;
    border: none!important;
}
.check-more {
    display: inline-block!important;
    padding: 4px 16px!important;
}
/*card*/
 .feed {
    padding-top: 0;
}
.f-single {
    margin: 0 16.5px 48px;
    width: 560px!important;
}
f-single-head {
    padding-bottom: 16px!important;
}
.f-item {
    padding: 16px 0;
}
.feed-v9 .f-single .fui-txtimg-bg {
    background-color: transparent!important;
}
.feed-v9 .f-single .fui-txtimg-bg::before {
    content:'';
    display: block;
    width: calc(100% - 32px);
    margin: 16px;
    border-top: 1px solid rgba(190, 190, 190, .3);
    height: 1px;
}
.feed-v9 .f-single .f-single-foot .f-op-detail.content-line {
    border-color: rgba(190, 190, 190, .3);
}
.txt-box {
    padding: 0 16px!important;
    margin-bottom: 16px!important;
}
.f-info {
    margin-bottom: 16px!important;
}
.fui-txtimg {
    padding: 0!important;
    margin-bottom: 0!important;
}
.f-item {
    padding: 0!important;
}
.feed-v9 .f-single .f-single-content .f-ct:not(:last-child) {
    padding: 0 0 16px!important;
}
.f-single .fui-txtimg .img-box {
    max-width: 100%!important;
}
.f-single .fui-txtimg .img-box a {
    display: block!important;
}
.feed-v9 .f-single .f-reprint {
    z-index: 10;
    position: relative;
}
.img-box-row.row-three .img-item:nth-child(3n), .img-box-row.row-two .img-item:nth-child(2n) {
    margin-right: 0!important;
}
.img-box-row.row-three .img-item:nth-child(3n), .img-box-row.row-three .img-item:nth-child(3n+2) {
    width: 185.5px!important;
}
.img-box-row .img-num {
    right: 0!important;
}
.f-video-wrap, .f-video-wrap .img-item, .f-video-wrap .j-videofeed-flashctn {
    width: 100%!important;
}
.feed-v9 .f-single .fui-left-right {
    /*     border-width: 0!important;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4)!important; */
    width: 560px;
    border: 1px solid #ddd;
    border-width: 1px 0!important;
    border-radius: 0!important;
    margin: 0!important;
}
.feed-v9 .f-single .f-single-foot {
    padding: 16px 0 16px;
}
.comments-box .mod-insert-img {
    right: 0px;
}
.visitor-avatar-list {
    display: flex;
    flex-wrap: wrap;
}
/*fab*/
 .fix-layout {
    right: 64px;
    bottom: 64px;
}
.gb-operation-area {
    width: 64px;
    border: none;
}
.gb-operation-area .gb-operation-button {
    height: 64px;
    background-color: #fff;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    box-shadow:0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.4);
    transition: box-shadow .2s ease;
}
.gb-operation-area .gb-operation-button:hover {
    box-shadow:0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12), 0 5px 5px -3px rgba(0, 0, 0, 0.4);
}
.gb-operation-area .gb-operation-button::before {
    content:'>';
    font-size: 24px;
    font-weight: bolder;
    font-family: monospace;
    transform: rotate(-90deg);
}
.gb-operation-area .gb-operation-button .gb-operation-icon, .gb-operation-area .gb-operation-button:hover .gb-operation-text {
    display: none;
}
div.icenter-right-mod[style="visibility:hidden;"] {
    display: none;
}
/*theme*/
 .layout-head .head-info, .layout-head .head-description a, .layout-head .head-name .user-name, .layout-head .head-detail-name .user-name, .layout-head .qz-progress-bar .progress-bar-info, .layout-head .weather-module, .layout-head .visit-module {
    color: #333!important;
}
.head-nav .head-nav-menu li a {
    color: #293233 !important;
}
}
