.kanji_popup td {
    cursor: default;
}
/* .kanji_popup .editable:hover {
    background-color: white;
} */

.kanji_popup input {
    font-weight: inherit;
    font-family: inherit;
    font-style: inherit;
    font-size: 80%;
    word-break: break-word;
}
.kanji_popup #strokes {
    visibility: hidden;
    /*     max-width: 640px;
    max-height: 85px; */
}
.kanji_popup #onyomi,
.kanji_popup #kunyomi {
    font-size: 24px;
    font-style: normal;
}
.kanji_popup #kanji {
    font-family: "KanjiStrokeOrders", "Yu Gothic UI", Times, "Times New Roman", Helvetica, "Helvetica Neue", Arial, "Yu Gothic UI", "ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro", Osaka, "メイリオ", Meiryo, "ＭＳ Ｐゴシック", "MS PGothic", "小塚明朝 Pro L", sans-serif !important;
    color: black;
    font-size: 160px;
    line-height: 160px;
    margin-right: 0.1em;
}
.kanji_popup #kanji:hover {
    font-family: 'Yu Mincho Demibold', sans-serif !important;
}
#kanjax_popup > table:nth-child(1) > tbody > tr > td:nth-child(2) {
    width: 100%;
}
#kanjax_popup > table.small {
    margin-top: 30px;
}
.kanji_popup #keyword {
    display: inline-block;
    color: #594681;
    font-size: 2rem;
    margin-bottom: 0.3rem;
    text-align: center;
    margin-left: 0px !important;
}
.kanji_popup #meaning {
    display: block;
    margin-bottom: 20px;
    color: white;
    background-color: black;
    line-height: 30px;
}
.kanji_popup #desc {
    margin-top: 200px;
    font-size: 1.5em;
}
#desc > i {
    color: purple;
    font-weight: bold;
}
#desc > i:before {
    content: '';
    width: 100%;
    height: 1px;
    display: inline-block;
    margin: 10px 0px;
    background: gray;
    /* Remove, just showing empty line */
}
#desc:after {
    content: '';
    width: 100%;
    height: 1px;
    display: inline-block;
    background: gray;
    /* Remove, just showing empty line */
}
.kanji_popup #yomi,
.kanji_popup #strokes {
    font-size: 40px;
    color: red;
    margin-top: 0.2em;
    margin-bottom: 2.4em;
}
.kanji_popup #onyomi {
    color: #DDC556;
    padding: 2px 4px;
    background-color: rgba(89, 52, 127, 1);
    border: 2px solid rgba(89, 52, 127, 1);
    border-radius: 5px;
    line-height: 40px;
}
.kanji_popup .japanese {
    font-family: "Kochi Mincho";
}
.kanji_popup .small {
    font-size: 16px;
}
.kanji_popup .medium {
    font-size: 19px;
}
.kanji_popup .large {
    font-size: 24px;
}
.kanji_popup .huge {
    font-size: 40px;
}
.kanji_popup .bold {
    font-weight: bold;
}
.kanji_popup .italic {
    font-style: italic;
}
.dict_popup {
    max-height: 400px;
    overflow: auto;
    width: 640px;
    background-color: #e0e0ff;
    border: 1px solid #ffffff;
    padding: 12px;
    border-radius: 17px;
    text-align: left;
}
.dict_popup .main {
    font-size: 160%;
}
.dict_popup .othf {
    color: #555;
    font-size: 70%;
}
.dict_popup .pos {
    color: #800;
    font-size: 70%;
    font-style: italic;
    margin-top: 0.5em;
    margin-left: -1.2em;
}
.dict_popup .misc {
    color: #555;
    font-size: 70%;
}
.dict_popup .refs {
    color: #555;
    font-size: 70%;
}
.dict_popup ol {
    margin-top: 0.3em;
    margin-bottom: 0.3em;
}
.dict_popup li {
    color: #345;
}
.dict_popup li span {
    color: black;
}
/* -------KANJI STROKES--------- */

#svgContainer {
    width: 95%;
    height: 140px;
    position: relative;
    padding-top: 20px;
    padding-left: 0.9375rem;
    padding-right: 0.9375rem;
    margin-bottom: 10px;
    overflow: auto
}
.KanjiFrames {
    height: 120px;
    display: block;
    background-color: white;
    overflow-x: auto;
    overflow-y: scroll;
    margin: 0 auto;
    padding: 0;
    max-width: none;
}
#svgContainer .KanjiFrames img {
    max-width: none;
    margin: 0 auto;
}
.KanjiFrames::after {
    box-sizing: border-box;
}
#svgContainer::after {
    box-sizing: border-box;
}
}
