css:
@-moz-document 
domain("lifehacker.com"), domain("gizmodo.com"),  domain("i.gizmodo.com"), domain("valleywag.com"), domain("gawker.com"), domain("jezebel.com"), domain("defamer.com"), domain("jalopnik.com"), domain("io9.com"), domain("deadspin.com"), domain("kotaku.com")

{
#sidebar, #splashPosts, #moreTopStories, #ad-300x250, .ad_twitter, iframe, #tags, #rightbar>table, .readmore, #footer>#siteinfo, #tagpageHeader, .related_posts
{display:none !important;}

#header
{background-position:0 !important; height:45px !important;}

#content_container, #container
{background:none !important; width:80% !important;}

.dateHeadline, .pager
{background:none !important; margin-left:-5%!important;}

.bigExcerptHolder, .bigExcerptLeft, .post
{background:none !important; width:100% !important; margin-top:1.5%!important}

.content
{margin-left:-5%!important; width:80% !important; border:solid 0px !important;}

#menubar
{margin-top:20px !important; width:100% !important; }

}
