@-moz-document domain("bitmarket.pl"), domain("bitmarket.net")
{
  body
  {
    background: #1C1C1C;
    color: #939393 !important;
  }

  .nav-pills > li > a
  {
    background: #41BB19;
    color: #FFFFFF;
  }

  body header div.container div.row div.span5
  {
    color: white !important;
  }

  body header,
  .nav-tabs > .active > a, .nav-tabs > .active > a:hover,
  .nav-tabs > .active > a:focus, .nav > li > a:hover, .lrform
  {
    background: #2C2C2C;
  }

  body div.navbar div.navbar-inner,
  .table-striped tbody > tr:nth-child(2n+1) > td,
  .dropdown-menu a:focus,
  .dropdown-menu a:hover
  {
    background: #3C3C3C;
  }

  body div.navbar
  {
    border: none !important;
  }

  div.span12 > p > object
  {
    margin-left: 0px;
    border: 2px solid #3C3C3C;
    border-radius: 10px !important;
    background: #2C2C2C;
  }

  svg image:nth-child(2)
  {
    opacity: 0.05;
  }

  .nav > li > a:focus, .nav > li > a:hover
  {
    background: #C3C3C3;
    color: #1C1C1C;
  }

  #all_event_sound
  {
    background: #41BB19;
  }

  button.btn.btn-primary
  {
    color: #C3C3C3 !important;
  }

  .minmax
  {
    stroke: #FFF;
    fill: #FFF;
  }

  .legend
  {
    stroke: none;
    fill: #41BB19;
  }

  .grid
  {
    stroke: #505050;
    stroke-width: 0.5;
  }

  h2, h3, h4, h5, h6, footer h6
  {
    color: #929292;
  }

  footer
  {
    background: #3C3C3C;
    color: #B1B1B1;
  }

  .navbar .nav > li > a, .navbar .nav > li > a:visited
  {
    color: #41BB19 !important;
  }

  header div.container div.row > div.span3 > center a,
  ul.nav.nav-tabs li a,
  div.text-center > a
  {
    color: #B1B1B1 !important;
  }

  .white-section
  {
    background: none;
  }


  ul.nav.nav-tabs li.active a
  {
    color: #41BB19 !important;
  }


  .dropdown-menu
  {
    border: 1px solid #3C3C3C !important;
  }

  .dropdown-menu a
  {
    background-color: #2C2C2C !important;
    border-bottom: 1px solid #3C3C3C !important;
    ;
  }
}
