css:
@-moz-document domain("reddit-stream.com") {

body {
    background: #e3e3e3 url(http://b.thumbs.redditmedia.com/7fh6_IjPWNmlQGKJxF7IA7tTDfdVRxx9tf1sr4AOb-w.png)no-repeat fixed center;
    background-position: calc(30% - 90px) 50%;
}

@media only screen and (max-width: 700px) {
    body {
        background-position: center center;
	}
}



div#user-bar {
    background: #000000 url(http://b.thumbs.redditmedia.com/C6I62V-mObEjaP91.png) -300px;
    padding: 4px;
}

.logo-heading {
    background:  url(http://e.thumbs.redditmedia.com/JpGywQkWiujBQ5YR.png)no-repeat;
    background-size: 80px 80px;
    height: 80px;
}

.logo-heading p {
    padding-left: 30px;
    color: #c0c0c0;
}

.logo-heading a {
    
    color: #cccccc;
    font-weight: bold;
}

div.payment-info a{
    color: #c0c0c0;
}

div.payment-info span {
    color: #c0c0c0;
}

div#user-bar .account-info {
    border-top: none;
}

@media only screen and (max-width: 1300px) {
    #user-bar .page-info {
    	float: left;
    }
    .account-info {
    	margin-left: -8px;
        float: right;
    }
}

div#post-sub a{
    color: #671cb7;
}

div#spoiler-delay a{
    color: #671cb7;
}

#post-info a {
        color: #671cb7;
}

a.reply-link, a.r-link {
	color: #671cb7;
}

div#user-bar .row {
    border-top: none;
}

#sidebar .button {
    border: 2px solid silver;
    color: silver;
    padding: 2px 4px;
}

@media only screen and (max-width: 700px) {
    #sidebar a.button {
        border: 2px solid silver;
        color: silver;
        padding: 2px 4px;
		margin-left: 10px;
        font-size: 8pt;
	}
    
}
.account-info {
    float: left;
    padding-left: 10px;
    margin-top: 5px;
}

.twitter-follow-button {
    float: right;
    display: none;
    
}

h1 {
    font-size: medium;
    font-weight: bold;
    color: #671cb7;
    margin-top: 2px;
}

h2 {
    font-size: medium;
    font-weight: bold;
    color: #808080;
    margin-top: 2px;
}

#post-info h3 {
    font-size: medium;
    font-weight: bold;
    color: #000000;
    margin-top: 2px;
}


h3 {
    font-size: medium;
    font-weight: bold;
    color: #000000;
    margin-top: 2px;
}


a {
    color: #0079d3;
}

a[href="/#Rocktober"],
a[href="/#rocktober"] {
    color: #4f007d!important;
    text-transform: uppercase;
    display: block;
    font-size: 32px;
    font-family: impact;
    font-style: normal;
    animation: intensify .1s steps(5) infinite;
    -webkit-animation: intensify .1s steps(5) infinite;
    -moz-animation: intensify .1s steps(5) infinite;
    white-space: nowrap;
    -moz-white-space: nowrap;
    pointer-events: none;
    cursor: default
}
@keyframes intensify {
    0% {
        margin-left: 0px
    }
    100% {
        margin-left: 3px
    }
}
@-webkit-keyframes intensify {
    0% {
        margin-left: 0px
    }
    100% {
        margin-left: 3px
    }
}
@-moz-keyframes intensify {
    0% {
        margin-left: 0px
    }
    100% {
        margin-left: 3px
    }
}
.side .md h2+ul {
    margin-top: -10px;
    padding-top: 20px;
    display: none
}
.side .md h2:hover+ul,
.side .md h2+ul:hover {
    display: block
}


#post-info table th {
    background: #4f007d !important;
    color: #ffffff;
    border: none;
    font-weight: bold;
    text-align: center;
}

.c-body {
    background: rgba(238, 238, 238, 0.7);
    border-radius: 4px;
}
}
