css:
@-moz-document url-prefix("http://www.sailorfuku.com/") {

html {background: url('http://image.noelshack.com/fichiers/2017/25/2/1497940508-berrypastel.png') !important;
}

#game_header a#logo { background: url('http://image.noelshack.com/fichiers/2017/37/7/1505653139-jquj2-2.png') !important;
    width: 173px;
}

#game_header { background-color: rgba(255, 186, 220, 0.6) !important;
        background-image: none;
}

#game_header #navigation li a { color: white;
      text-shadow: 2px 1px rgba(0, 0, 0, 0.27);
}

#game_header a:hover {
  background-color: rgba(221, 101, 151, 0.77);
}

#game_header #navigation li.nav-button a, #game_header #navigation li.nav-button span {
    color: #b08394;
    line-height: 34px;
}
    
#game_header #navigation-extra li.nav-button a, #game_header #navigation-extra li.nav-button span {
    display: block;
    color: #b08394;
    text-decoration: none;
    line-height: 34px;
    padding: 0 10px;
    margin: 0 3px;
    position: relative;
}    
    
#game_header a:hover {
    background-color: rgba(255, 164, 209, 0.6);
}
    
#game_header #navigation li a {
    color: white;
    text-shadow: 2px 1px rgba(15, 0, 0, 0.07);
}

#game_header #navigation .notification-count {
    border-radius: 0.25em;
    font-style: normal;
    font-size: 8px;
    background: #e45a94;
    color: white;
    font-weight: normal;
    display: block;
    line-height: normal;
    padding: 0 3px;
    position: absolute;
    top: 3px;
    left: 31px;
    font-family: verdana;
    text-align: center;
    z-index: 1;
}
    
.menu-links .spch-bub-inside em {
    border-radius: 0.25em;
    font-style: normal;
    font-size: 12px;
    background: #e45a94;
    color: white;
    padding: 0 0.4em;
    position: relative;
    vertical-align: super;
}
    
.menu-links .spch-bub-inside .point {
    width: 0;
    height: 0;
    position: absolute;
    top: 0.4em;
    left: 0.4em;
    display: block;
    border-left: 0.45em solid #e45a94;
    border-bottom: 0.45em solid #FFF;
    border-bottom: 0.45em solid transparent;
    overflow: hidden;
    }    
    
.menu-links a {
    background: rgb(166, 233, 209);
    color: white;
    background-image: none;
    box-shadow: 0 0 7px #FFFFFF;
}
.menu-links a:hover {
    text-decoration: none;
    background-image: linear-gradient(#86e2c9, #63c9be);
    color: white;
}

.menu-links a.active { 
    color: #fea7c5;
    background: white;
}

.profile-calendar table td.ok span {
    display: block;
    background-color: #a6e9d1;
    color: white;
    font-weight: bold;
    height: 100%;
    line-height: 200%;
}
    
.profile-calendar table td.ok span:hover {
    background-color: #63c9be;
    cursor: pointer;
}
    
#contentContainer {
   background: rgba(255, 255, 255, 0.49);
}

#world_map #map_place_name {
    background: rgba(255, 255, 255, 0.49);
}
#notifications .notification {
     background: rgba(182, 208, 255, 0.8);
    color: white;
    box-shadow: 0 0 7px #FFFFFF;
}
    #notifications .notification:hover {
     background: rgb(113, 154, 229);
    color: white;
     box-shadow: 0 0 7px #FFFFFF;
}
    .menu-ressources {
          background: rgba(255, 182, 210, 0.04);
          background-image: none;
          box-shadow: none;
                                        }
#notifications .filler { display: none;}

a {
    color: #149083;
}

div.bubble .text {
    color: #d56186;
      background-color: rgba(255, 255, 255, 0.62);
}

a:hover, .link:hover {
    color: #ffccdc;
    cursor: pointer;
} 
    
.profile-wishlist .wishlist-more {
    display: block;
    width: 134px;
    height: 163px;
    float: left;
    margin: 5px;
    font-family: Comfortaa;
    font-size: 200%;
    text-align: center;
    line-height: 147px;
    border-radius: 5px;
    padding-right: 13px;
    box-sizing: border-box;
    color: #d56186;
    background: white;
    box-shadow: 0 0 3px #A3A3A3;
    border: 5px solid white;
}
    
div.tendances div.excellent {
      background: rgba(255, 255, 255, 0.75);
}

.avatar_resized { border-radius: 5px;}
DIV.inew {
  BORDER-COLOR: #14A110 #4DC5AA #145E25 #69CDAF;
}

.profilRessources img[src*="http://www.sailorfuku.com/static/images/background/marron_fleur.png"] {
    opacity: 0.4;
}
    
.profilRessources {
      background-color: rgba(163, 231, 152, 0.68);
    border: 2px solid #91CED3;
    
}
.articleCadre .data {
      background: #91CED3;
    color: white;
}
.profilRessources h4 {
  margin-top: 12px;
  padding-top: 10px;
  font-size: 14px;
  font-weight: normal;
  color: #FFFFFF;
  line-height: 14px;
  font-family: 'Georgia';
  font-size: x-large;
}

#world_map #map_place_name .map_place_name {
    color: rgb(219, 122, 144);
    font-family: 'Time New';
}

.menuDecoration {
  background: rgba(164, 255, 79, 0.21);
}

#game_status {
    background: none;
    box-shadow: none;
}
#amies2 .amie {
      background-color: #FFB6CA;
      border: 3px double #FFD0E2;
}
#amies2 h4 {
    color: #FFB6CA
}

#amies2 .amiesListe { 
background-color: rgba(255, 255, 255, 0.49);
    border: 2px solid #71B281;
}
#amies2 a.infos {
    background-color: rgba(113, 178, 129, 0.79);
    color: white;
}
.msg {
      background-color: #CDF4D0;
}

#amies2 .changement {
    color: #DCFF5C;
}
/*Tout le css a été produit par QuentinChtl - Merci de respecter mon travail*/
#amies2 .amiesListe .data {
     background-color: rgba(109, 208, 122, 0.55);
}
span.spch-bub-inside em {
    background: #ed84b0;
}

.profile-clubs-list .separator {
    color: #ed84b0;
} 

.profile-clubs-list a:hover {
    text-decoration: none;
    border: 0;
    background: #f38ab3;
    border-radius: 2px;
}    
    
.bubble .bloc .avatar {
    border: none;
}
div.bubble .bloc .auteur_avatar {
    border: none;
}

.menu-bottom .profile-interaction button.like {
  height: 50px;
  background-image: -webkit-linear-gradient(#F7ACD7,#CB5B8E);
      border: 2px solid #FFFFFF;
    text-shadow: none;
    
}
.menu-bottom .profile-interaction button.like:hover  {
  height: 50px;
  background-image: -webkit-linear-gradient(#CB5B8E,#F7ACD7);
      border: 2px solid #FFFFFF;
    text-shadow: none;
}

.menu-bottom .profile-interaction button {
  background-image: -webkit-linear-gradient(#F7ACD7,#CB5B8E);
      border: 2px solid #FFFFFF;
    text-shadow: none;
}
.menu-bottom .profile-interaction button:hover {
  background-image: -webkit-linear-gradient(#CB5B8E,#F7ACD7);
      border: 2px solid #FFFFFF;
    text-shadow: none;
}

body {
    font-family: "lucida grande",tahoma,verdana,arial,sans-serif;
    color: #cb6083;
    font-size: 12px;
    text-align: left;
}
    
.pun .blocktable th {
    background: #ebf1f5;
    border-color: #a3dbd5;
    color: #49b0a5;
}
    
.pun .inew .icon {
    border-color: #a9f6d5 #97e7d2 #75d6c4 #64c9bf;
}
    
#punindex .blocktable h2, .pun #vf h2 {
    color: #49b0a5;
}
    
 #blocAFaire a {
    color: #d56186;
}  
    
#blocAFaire a.done {
    color: #63c9be;
}  
    
#blocAnnonces a.annonce .date {
    background: #63c9be;
    color: white;
    width: 70px;
    font-size: 11px;
    padding: 2px 3px;
    text-align: center;
    border-radius: 2px;
}  
    
#blocAnnonces a.annonce .intitule {
    color: #63c9be;
    padding-left: 3px;
}
  
#jeuContenu {
    background: rgba(241, 255, 200, 0.96);
}



}

@-moz-document url-prefix("http://www.sailorfuku.com/hito.profil.avatar.html?id=") {
html {
  background: url('http://www.zupimages.net/up/15/1ng') !important;
}
}
