}
.sidebar a.selected,
.react-toggle .react-toggle-track,
.react-toggle-track div {
    background-color: #2b90d9!important;
}
.sidebar a:hover,
.react-autosuggest__container input,
.app-holder>div,
.admin-wrapper,
.sidebar-wrapper,
.admin-wrapper .sidebar-wrapper ul ul {
    background-color: #fff!important;
}
input.search__input,
input.search__input:focus,
.flash-message,
.load-more:hover,
.column,
.landing-strip,
.search-results__header {
    background: #f0f0f0;
}
.search__icon .fa,
.search__input,
.account__action-bar__tab span {
    color: #000;
}
@media screen and (max-width: 1024px) {
    .column:last-child, .drawer:last-child {
        margin-right: 0;
    }
    .column:first-child,
    .drawer:first-child {
        margin-left: 0;
    }
}
@media (min-width: 1025px) {
    .column {
        margin-bottom: 10px;
        margin-right: 5px;
        margin-top: 10px;
    }
}
@media screen and (min-width: 1900px) {
    .column, .drawer {
        margin-top: 2vh;
        padding-bottom: 0px;
        padding-left: 5px;
        padding-right: 5px;
        padding-top: 0px;
    }
}
.table>tbody>tr:nth-child(odd)>td,
.table>tbody>tr:nth-child(odd)>th {
    color: white;
}
.spoiler-button,
.status__video-player-spoiler {
    z-index: 100;
    top: 4px;
    left: 4px;
    position: absolute;
}
.media-gallery__item-thumbnail {
    background-size: auto;
}
.column-header__button.active,
.column-header__button.active:hover {
    background: #f0f0f0 !important;
    color: #9baec8 !important;
}
