    overflow: hidden;
    white-space: pre-wrap;
}
.status__content--no-action p {
    font-size: 17px !important;
}
.status__content__spoiler-link {
    border-radius: 2px;
    background: transparent;
    border: 0;
    color: #fff;
    font-weight: 300;
