    text-transform: lowercase;
    line-height: inherit;
    cursor: pointer;
    float: right;
    margin-top: 2px;
}
.compose-form__buttons-wrapper {
    background-color: #fff;
}
.character-counter__wrapper {
    line-height: 36px;
    margin-right: 16px;
    padding-top: 10px;
}
.character-counter {
    font-size: 13px !important;
}
.button.button--block {
    display: block;
    width: 36px;
    height: 36px;
    border-radius: 90px;
    padding: 0px !important;
    font-size: 10px;
    margin-right: 10px;
    margin-bottom: 10px;
}
.compose-form__publish-private {
    font-size: 8px !important;
}
.compose-form__buttons {
    display: -webkit-inline-box;
    background: white!important;
    box-shadow: none!important;
    margin-bottom: 0px;
    margin-top: 6px;
}
.column-header {
    margin-top: -10px;
    margin-left: -5px;
    margin-right: -10px;
    background-color: #f0f0f0 !important;
}
#Getting-started {
    margin-top: -10px;
    margin-left: -5px;
    margin-right: -10px;
    background-color: #f0f0f0 !important;
}
::-webkit-scrollbar-thumb {
    background: #2588d0 !important;
}
::-webkit-scrollbar-thumb:hover {
    background: #419bdd !important;
}
