    margin-left: -5px;
    margin-right: -10px;
    background-color: #f0f0f0 !important;
}
.column-back-button {
    margin-top: -10px;
    margin-left: -5px;
    margin-right: -10px;
    background-color: #f0f0f0 !important;
}
.detailed-status {
    background-color: #f0f0f0 !important;
}
.detailed-status__action-bar {
    border-top: 0px solid #393f4f;
    background-color: #f0f0f0 !important;
}
.dropdown--active .dropdown__content>ul>li>a:hover {
    background: #419bdd !important;
    color: #fff !important;
}
.dropdown--active .dropdown__content>ul>li>a {
    color: #000;
}
.dropdown--active .dropdown__content>ul,
.dropdown__content-list-link {
    background: #fff !important;
}
.empty-column-indicator {
    background: #f0f0f0 !important;
    color: #323232 !important;
}
