css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#collapsedfromBox,
#expandedHeaderView,
#collapsedHeaderView,
#expandedfromBox {
  padding-top: 0px !important;
}

header-view-button-box {
  padding: 0px !important;
  border: 1px solid rgba(0,0,0,0.25);
  -moz-box-shadow: 0px 0px 5px black;
  -moz-border-radius-bottomleft: 5px;
}

.msgHeaderView-button {
  padding: 0px !important;
  margin: 0px !important;
  background-color: transparent !important;
  border: none !important;
  -moz-border-radius: 0px !important;
}

.msgHeaderView-button:hover {
  background-color: rgba(0,0,0,0.25) !important;
}

#collapsedButtonBox .msgHeaderView-button .button-text,
#expandedButtonBox .msgHeaderView-button .button-text {
  display: none;
}

.hdrTrashButton[trash="true"] {
  -moz-image-region: rect(0px, 142px, 16px, 126px) !important;
}

#messagepanebox {
  margin: 20px;
  -moz-box-shadow: 5px 5px 15px rgba(0,0,0,0.5);
  background-color: transparent !important;
}
#msgHeaderViewDeck {
  background-color: -moz-field;
  border-bottom: 1px solid black;
}

#threadpane-splitter,
#attachment-splitter {
  -moz-appearance: none !important;
  border-left: 3px solid blue !important;
  -moz-border-left-colors: transparent transparent threedshadow !important;
  border-right: 2px solid transparent !important;
  -moz-border-right-colors: rgba(0,0,0,0.01) !important;
  margin: 0px -2px 0px -2px !important;
  max-width: 3px;
  background-color: transparent !important;
  -moz-box-shadow: 0px 1px 5px rgba(0,0,0,1);
}
#threadpane-splitter[orient="vertical"],
#attachment-splitter[orient="vertical"] {
  -moz-appearance: none !important;

  border-bottom: 3px solid blue !important;
  -moz-border-bottom-colors: transparent transparent threedshadow !important;
  border-left: none !important;
  -moz-border-left-colors: none !important;
  border-right: none !important;
  -moz-border-right-colors: none !important;
  border-top: 2px solid transparent !important;
  -moz-border-top-colors: rgba(0,0,0,0.01) !important;

  margin: -2px 0px -2px 0px !important;
  max-height: 3px !important;
  max-width: 1000px !important;
  background-color: transparent !important;
}
#attachmentView {
  min-height: 75px !important;
}

.hdrArchiveButton {
  list-style-image: url(data:image/png;base64,);
}

.hdrForwardButton {
  list-style-image: url(data:image/png;base64,);
}
.hdrReplyButton {
  list-style-image: url(data:image/png;base64,);
}

.hdrJunkButton {
  list-style-image: url(data:image/png;base64,);
}
