css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("eow.alc.co.jp") {
/* */
  #AreaHeaderLeft {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  }
  #AreaHeaderLeft #logo a img { max-height: 25px !important; }
  #HomeButton, #ShopButton { padding-top: 0px !important; }
  #HomeButton a img, #ShopButton a img {
    height: 12px !important;
    width: 30px !important;
  }
  
  /* */
  .pro_button { display: none !important; }
  p.pr_text {
    display: none !important;
    line-height: 0px !important;
  }

  /* */
  .search_box {
    height: 30px !important;
    padding-right: 100px !important;
  }
  .search_box .s_txt1 {
    height: 26px !important;
    line-height: 27px !important;
    margin-top: 2px !important;
  }
  .search_box .s_btn {
    width: 38px !important;
    margin-top: -26px !important;
    right: 47px !important;
  }
  .search_box .reset {
    height: 28px !important;
    margin-top: -27px !important;
  }
  #tabbody .search_box {
    margin-bottom: 0px !important;
  }
  
  /* */
  #fixedBox {
    position: absolute !important;
    top: 30px !important;
    height: 28px !important;
    padding-top: 0px !important;
    border-bottom: unset !important;
  }
  
  /* */
  #searchHeader {
    height: 0px !important;
    margin-bottom:  0px !important;
  }
  .log { display: none !important; }
  #searchHeader .setting { margin-top: -25px !important; }
  
  /* */
  #AreaUpperLeft { margin-top: -65px !important; }
  #sw {
    padding: 5px 0 5px 15px !important;
    border-top: unset !important;
    margin-top: 40px !important;
  }
  .itemPostion {
    margin-top: 0px !important;
  }
  #paging {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  }
  div.sas {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
  }
  #resultsList ul img {
    margin-top: -4px !important;
    padding-left: 9px !important;
    width: 25px !important;
    height: 25px !important;
  }
}

@-moz-document domain("eow.alc.co.jp") {
/* 追加 */
#resultsArea p { display: none; /* PR */}

#introduceEowp {
    margin: 0;
    padding: 0;
    display: none;
}

/* dec blank */
#AreaUpperRight { height: auto; }
#AreaUpperLeftContainer > br { display: none; }
#AreaUpperLeftContainer br { font-size: 50%; }

#footer {
    padding-top: 9px;
    padding-bottom: 9px;
    font-size: 100%;
    line-height: 1.1em;
}
#footer * { padding: 0; }
#footer3 { margin-bottom: 3px; }

}
