css:
@-moz-document domain(www.youtube.com) {
div#container.style-scope.ytd-searchbox{
background-color: #2d4f76;
border-radius: 20px;
border: none;
outline: none;
box-shadow: none;
}
.style-scope.ytd-section-list-renderer{
background-color: white;
width: 887px !important; 
}
.style-scope.ytd-shelf-renderer{
  top: 10px; 
  padding: 5px;
}
.style-scope.yt-horizontal-list-renderer.arrow.style-default{

}
/*margin: 13px auto 0;*/
div#container.style-scope.ytd-searchbox, ytd-guide-entry-renderer.style-scope.ytd-guide-section-renderer{
  -webkit-transition: all 1s ease;
     -moz-transition: all 1s ease;
       -o-transition: all 1s ease;
      -ms-transition: all 1s ease;
          transition: all 1s ease;
}
.style-scope.ytd-masthead{
background-color: #4f739a;
}
div#container.style-scope.ytd-searchbox:hover {
background-color: #FFFFFF;
border-radius: 20px;
border: none;
outline: none;
}
div#masthead-ad.style-scope.ytd-browse{
    display: none;
content: url('https://vk.com/images/icons/dev_head_magglass.png');
}
div#guide-inner-content.style-scope.ytd-app{
background: #eeeff1;
}
  div#container.style-scope.ytd-searchbox:before{
    content: url('https://vk.com/images/icons/dev_head_magglass.png')' ';
    margin: 5px;
  }
span.title.style-scope.ytd-guide-entry-renderer{
color: #4a6a7d;
}
yt-icon#logo-icon{
content: url('https://pp.userapi.com/c836123/v836123650/57cd3/w73hQKh5cbU.jpg');
    margin: 5px;
    padding: 5px;
}
yt-icon.guide-icon.style-scope.ytd-guide-entry-renderer{
color: #9ab2c7;
}
span#country-code{
display:none;
}
ytd-browse.style-scope.ytd-page-manager{
background: #eeeff1;
}
span.guide-entry-count.style-scope.ytd-guide-entry-renderer{

}
::-webkit-scrollbar {
    width: 10px;
}
 
::-webkit-scrollbar-track {
     background: black;
    border-radius: 2px;
}
 
::-webkit-scrollbar-thumb {
    border-radius: 8px;
    background: #C0C0C0;
    width: 9px;
    
}
span.guide-entry-count.style-scope.ytd-guide-entry-renderer{
		text-align: center;
		background-color: #3970B8;
		padding: 0 !important;
    border: none;
	}

	/* Ð¡ÑÐµÑÑÐ¸ÐºÐ¸ ÑÐ»ÐµÐ²Ð° */
span.guide-entry-count.style-scope.ytd-guide-entry-renderer{
    height: 24px !important;
    min-width: 24px !important;
    width: auto !important;
    padding: 0 5px !important;
    line-height: 24px;
    margin: 2px 2px 0 0 !important;
    border-radius: 15px;
    box-sizing: border-box;
    color: #fff;
  }

/*div#end.style-scope.ytd-masthead{
display:none; #4a6a7d
}*/
button#search-icon-legacy, span#gs_ok50{
display:none;
}

paper-button#button.style-scope.ytd-button-renderer.style-text:hover{
background-color: #EBEBEB;
}
paper-button.style-scope.ytd-subscribe-button-renderer{
background-color: #5e82a9;
}
paper-button.style-scope.ytd-subscribe-button-renderer:hover{
background-color: #6889ae;
}
paper-button.style-scope.ytd-subscribe-button-renderer:active{
background-color: #577aa2;
}
div.ytp-progress-bar-container{
border-radius: 100px;
}
div.ytp-play-progress.ytp-swatch-background-color{
background-color: #2d4f76;
}
.html5-play-progress, .ytp-play-progress {
   border-radius: 100px;
}

.html5-load-progress, .ytp-load-progress {
   border-radius: 100px;
}

.html5-scrubber-button, .ytp-scrubber-button {
border-radius: 10px;
background-color: #2d4f76;
   width: 21px !important;
   height: 21px !important;
   border: none !important;

   margin-top: 0px !important;
}
.html5-progress-bar-container, .ytp-progress-bar-container {
   height: 12px !important;
}
.html5-progress-bar, .ytp-progress-bar {
   margin-top: 12px !important;
}
.html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list {
   height: 12px !important;
}
.ytp-volume-slider-track {
   background: #0C4177 !important;
}


yt-formatted-string.style-scope.ytd-subscribe-button-renderer{
    color: white;
    font-size: 108%;
    font-weight: 10;
}
span.deemphasize.style-scope.yt-formatted-string{
	color: #FFC0CB;
}

a.style-scope.ytd-toggle-button-renderer  {
  position: relative;
  display: inline-block;
  font-size: 80%;
  font-weight: 700;
  color: rgb(209,209,217);
  text-decoration: none;
  text-shadow: 0 -1px 2px rgba(0,0,0,.2);
  padding: .0em 1em;
  outline: none;
  border-radius: 3px;
  background: #5e82a9;
    margin: 5px;
}
a.style-scope.ytd-toggle-button-renderer:hover:not(:active) {
  background: #6889ae;
}
a.style-scope.ytd-toggle-button-renderer:active {
  top: 1px;
  background: #577aa2;
}
ytd-toggle-button-renderer#like-button.style-scope.ytd-comment-action-buttons-renderer.style-text{
margin: 5px;
}
}
