@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ictooce.ugent.be") {
    .playerContainer_controls_playback_playbackBar_canvas {
        z-index: 1000;
    }
    #playerContainer_controls_playback_playbackBar_timeControl {
        text-shadow: 0px 0px 2px black;
    }
    .playbackBar {
        padding-top: 2px;
        box-shadow: none !important;
        background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGNiqAcAAIgAgoGu+rEAAAAASUVORK5CYII=") repeat, url("data:image/png;base64,") bottom repeat-x !important;
        height: 21px;
    }
    .playbackBarFull {
        background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGNiqAcAAIgAgoGu+rEAAAAASUVORK5CYII=") repeat, url("data:image/png;base64,") repeat-x !important;
        position: relative;
    }
    .playbackBarFull:after {
        content: " ";
        background: url("data:image/gif;base64,") !important;
        display: block;
        background: red;
        width: 34px !important;
        height: 21px !important;
        position: absolute;
        right: -9px;
        top: -4px;
    }
}
