css:
@-moz-document domain("fanfiction.press"), domain("fictionhub.io") {
html,
body {
    background-color: #111;
}

header {
    background-color: #555;
}

article {
    background-color: #333;
}

article h1,
article h2 {
    color: white;
}

p {
    color: #BBB;
}

article .post-footer {
    background-color: #333;
}

.btn-default {
    color: #BBB;
}

.label-default {
    background-color: #555;
}

.panel,
article .panel {
    background-color: #555;
}

.home .subnav {
    background: #333;
}

.home .subnav a {
	color: #999;   
}

.subnav {
    background: #333;
}

.subnav .btn {
    background: #555;
}

.btn-cta,
.btn-cta-large {
    background: #555;
}

.dropdown-menu {
 	background-color: #333;   
}

.dropdown-menu>li>a {
 	color: #999;   
}

.home .header .btn {
    border: 0.5px solid #666;
}
}
