css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); 


/* OPTIONAL: UNCOMMENT THE LINE BELOW TO REMOVE THE LIGHT BORDER ABOVE THE NAVBAR */
/* #TabsToolbar:not([collapsed="true"]) + #nav-bar { box-shadow: none !important; margin-top: 0 !important; } */


/*navbar #####################################################*/

#nav-bar *,
#TabsToolbar:not([collapsed="true"]) + #nav-bar {
    background-color: transparent !important;
    border-color: transparent !important;
}


/*tabs #######################################################*/

.tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox {
    padding: 0 !important;
}
#TabsToolbar *,
.tabbrowser-tab * {
    text-shadow: none;
}
.tabbrowser-tab:not([pinned]) {
    max-width: 310px;
}

.tab-background
{
  -moz-margin-end: 0!important;
  -moz-margin-start: 0!important;
  background: transparent!important;
}
.tab-background-start,
.tab-background-end {
    display: none !important;
}
#TabsToolbar .tab-background-middle {
    background: transparent!important;
    margin: -12px 0!important;
}
.tabbrowser-tab:hover ,
.tabbrowser-tab[selected] {
    background-color: rgba(255,255,255,0.4) !important;
}

.tab-close-button {
    padding: 16px !important;
    margin-inline-end: -9px !important;
    margin-top: -1px !important;
}
.tabbrowser-tab .close-icon:hover {
    background-image: -moz-image-rect(url("chrome://global/skin/icons/close.svg"), 0, 16, 16, 0) !important;
    background-color: rgba(255,255,255,0.8);
}


/*newtab button ##############################################*/

.tabs-newtab-button {
    background-image: none! important;
    margin: 0 !important;
    width: 50px !important;
}
.tabs-newtab-button:hover {
    background-color: rgba(255,255,255,0.8) !important;
    background-image: none! important;
}
