css:
@-moz-document url-prefix("http://help.autodesk.com/view/fusion360/") {
/*@import url('https://fonts.googleapis.com/css?family=Roboto:400,500,700');*/

body {
	margin-top: 50px;
}

#ui-user-area {
 	box-shadow: 0 2px 4px rgba(0,0,0, .4);
    position: fixed;
    top: 0;
    width: 100%;
}

#ui-content-area .ui-sidebar {
 	width: 400px;
    margin-left: 25px;
}

html[data-layout=fixed] .ui-contents-panel {
	max-height: calc(100vh - 50px);
    margin: 0;
}

.node-tree-overflow {
    max-height: calc(100vh - 50px);
}

#adsk-athena[data-current-source] #eos-footer {
    display: none;
}

#ui-header {
    display: none;
}

#adsk-athena .grid {
    max-width: 100%;
}

#ui-content-area .ui-sidebar~* {
    margin-left: 425px;
}

#ui-main-area.browser {
	overflow: hidden;
}

#ui-content-area .ui-content-wrap {
    overflow: auto;
    height: calc(100vh - 108px);
    box-sizing: border-box;
}

#ui-content-area .ui-before-content {
    height: 50px;
    color: white;
    background: #333;
    box-shadow: 0 2px 4px rgba(0,0,0,.3);
}

.wbh-caas-viewer {
    background: transparent;
}

#ui-user-area .nav .identity {
	min-width: 120px;
}

h2.node-tree-section {
    margin: 2rem 25px 1rem 0;
    border-bottom: solid 1px rgba(0,0,0,.25);
}

.wbh-caas-viewer a:active, .wbh-caas-viewer a:link, .wbh-caas-viewer a:visited {
    font-family: 'Roboto', sans-serif;
    letter-spacing: 1px;
    font-weight: 500;
}

.node-tree .node[aria-selected=true]>a span {
    font-family: 'Roboto', sans-serif;
    font-weight: 500;
}

nav.ui-breadcrumbs ul li a[href]:focus, nav.ui-breadcrumbs ul li a[href]:hover {
    color: whitesmoke;
}

#ui-user-area .grid ul.gridFull {
    position: static;
}
}
