css:
@-moz-document url-prefix('https://fontawesome.com/v4.7.0/icons/') {
#social-buttons,
.jumbotron-ad,
#wrap .fa5,
#wrap .stripe-ad,
.algolia {
	display: none;
}

.container .row:nth-child(2) .col-md-10 {
	width: 100% !important;
}

/* clean */
div#banner,
div.navbar.navbar-static-top,
div.jumbotron,
div#social-buttons,
p.lead,
#footer.footer {
	display: none !important
}

/* icon size */
.fontawesome-icon-list .fa-hover a {
	height: auto;
	padding: 10px;
	line-height: normal;
}

i.fa {
	font-size: 4em !important;
	margin-right: 40px !important
}

#search i.fa {
	font-size: 1em !important;
}


}
