css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?:\/\/v.ls-rp\.es(?!\/admin\/?)(?!\/lspd\/admin?).*") {

#content {
    background: #000000;
}
.post.classic {
    background: #22232B;
}
.post_body {
    color: #C0C1BC;
}
.post_controls {
    background: #424549;
}
.postbit_buttons > a:link {
    color: #ffffff;
}

.postbit_buttons > a:hover {
    background: #b54345;
    border: 1px solid #b54345;
}

.trow1,
.trow2 {
    background: #22232B;
}
.trow1 > a:link,
throw1 > a:visited {
    color: #9A9592;
}
.trow2 > a:link,
throw2 > a:visited,
.postbit_buttons > a:visited,
.postbit_buttons > a:active {
    color: #9A9592;
}
.forum_off i {
    color: white;
}
.forum_on {
    color: #aa454d;
}
#panel .lower a:link,
#panel .lower a:visited,
#panel .lower a:hover,
#panel .lower a:active {
    color: #d3d3d5;
}
#panel .lower {
    background: #22232b;
}
#footer .uppermid {
    background: #36393e;
    color: #fff;
    border-top: 3px solid #b54345;
}

#footer .uppermid  .ft-head {
    color: #fff;
    font-size: 16px;
    font-weight: 400;
}


#footer .uppermid  .ft-col-1 h2 {
    color: #fff;

}


#footer .lower {
    background: #22232b;
    color: white;
    border-top: 2px solid rgb(181, 67, 69);
}

#footer .lower a:link, #footer .lower a:visited{
    color: #b54345; 
}

.tcat {
    background: #36393E;
}

.smalltext {
    color: #72767D;;
}
table {
    color: #fff;
}

.subforum_minion {
    color: #f3f3f3;
}

.subforum_minioff {
    opacity: .5;
    color: white;
}

-and-more  a {
    color: white;
}

.thead {
    background: #303037;
}

.post_body a:visited {
    color: #2A7ECF;
}

.post_body a:link {
  color: #2A7ECF;
}

a:visited {
    color: #ffffff;
}

a:link {
    color: #ffffff;
}

.post_body a:hover {
   text-decoration: underline;
}

#panel .upper {
    background: #000000;
}

.navigation {
       background: #36393e; 
}

blockquote {
    background: #303037;
}
blockquote blockquote{
    border: 1px solid #eee !important;
    background: #303037;}

blockquote blockquote blockquote{
    border: 1px solid #eee !important;
    background: #303037;}

blockquote blockquote blockquote blockquote{
    border: 1px solid #eee !important;
    background: #303037;}

blockquote blockquote blockquote blockquote blockquote{
    border: 1px solid #eee !important;
    background: #303037;}

[class*="hot"]:after {
    color: #555;
}

[class*="new"]:after {
    color: #fc6042;
}

.reputation_negative {
    display: none;
}

.mpage a, .mpage a:link, .mpage a:visited, .mpage a:focus {
    background: rgb(48, 48, 55);
    color: #eceff1;
    border: 1px solid #36393e;
}

.trow1 .mpage, .trow2 .mpage {
    opacity: 1; }

.mpage a:hover {
    background: #dddddd;
    color: black;
    font-weight: bold;
    border: 1px solid #dddddd;
}

.trow_sep {
    background: #36393E;
    color: #fff;
}

.tcat a {
color:#fff;
}

.tfoot {
    padding: 10px;
    color: #777;
    background: #36393e;
}

.new_thread_button {
   background: #B54345 !important;
}

input.button {
   background: #B54345;
}

input.button:hover {
   background: rgb(150, 52, 53);
}

.profcontainer .left-sidebar .sidebar-container {
    background: #36393e;
    color: white;
    border: 1px solid rgb(54, 57, 62);
}

.aroptions {
background-color: #B54345 !important;
}

 .profheading {
    background: #36393e !important;
    border: 1px solid #36393e !important;
    color: white !important;
}

.profcontainer ul.tabs li.current {
    background: #b54345 !important;
    color: #fff !important;
}

.profcontainer ul.tabs li {
    color: #fff !important;
}

.profcontainer .tabs-wrap {
    border: 1px solid #b54345;
    color: white;
}

.tab-content {
    background: #36393e;
}

.profcontainer  ul.tabs {
   background: #36393e;
 }

.profcontainer ul.tabs li:hover {
    background: #696969;
}

[class*="add_buddy_button"]:hover, [class*="add_ignore_button"]:hover {
    background: #696969 !important;
}

.profile-ctbox {
    border-bottom: 1px solid rgb(82, 87, 94);
}

.thead.side {
    background: #303037;
}

.thead a {
    color: #fff;
    font-size: 13px;
    font-family: "Open Sans", Arial, Tahoma, sans-serif;
}

.fw4 a {
color: #0072bc;
}

.lastpost a {
color: #0072bc;
}

.closed_button {
background: #B54345 !important;
}

#backtop {
  background: rgba(181, 67, 69, 0.80);
}

#backtop:hover {
  background: rgba(150, 52, 53, 0.80);
}


.new_reply_button {
  background: #B54345 !important;
}

.new_reply_button:hover {
    background: rgb(150, 52, 53) !important;
}

.pmbg {
    background: #b54345;
}

.pmbg:hover {
    background: rgb(150, 52, 53);
}

.pagination a {
    background: #b54345;
    border: 1px solid #b54345;
}

.pagination .pagination_current {
    background: #36393e;
    border: 1px solid #f1f1f1;
}

.pagination a:hover {
    background: #36393e;
    border-color: #f1f1f1;
}

.pagination a:link {
    color: #fff !important;
}

.modal > div{
overflow-y:visible !important;
}

.tfoot a {
    color: #fff !important;
}

.tfoot a:hover {
    color: #fff !important;
}

.tborder {
    background: inherit;
}

.pagination .pages {
    color: white;
}

.author_information .smalltext {
color : #b5b5b5;
}

.post .post_author div.author_statistics {
color: #b5b5b5;
}

}
