css:
@-moz-document domain("yummyanime.club") {
::-webkit-scrollbar{width:12px;height:12px}
::-webkit-scrollbar-track-piece{background:#232323}
::-webkit-scrollbar-thumb:vertical{height:50px;background:rgba(68, 68, 68  , 1)}
::-webkit-scrollbar-thumb:vertical:hover{background:#FF6666;}
::-webkit-scrollbar-thumb:horizontal{width:50px;background:rgba(68, 68, 68  , 1)}
::-webkit-scrollbar-thumb:horizontal:hover{background:#FF6666;}

.top {
    /*[[top-style]]*/
    
    height: 150px;
    position: relative;
    padding: 25px 0 0 0;
}

body {
    /*[[body-style]]*/
    
    color: #f0f0f0;
}

.login-form {
    position: absolute;
    left: 0px;
    top: 41px;
    background: rgba(35, 35, 35, 0.82);
    border: 1px solid #333333;
    padding: 10px;
    width: 220px;
    z-index: 2;
    text-align: center;
    display: none;
}

.login {
    background: rgb(37, 37, 37);
}

.login a {
	color: #ff6666;
}

.login span {
    color: #f5f5f5;
}
    
.admachina-banner {
    margin-bottom: -200px;
}

.slide-item {
    border-right: 0px;
}

.last-video-head {
    border-bottom: 1px solid #ff6666;
}

.sub-menu a {
    border-bottom: 1px solid #424242;
}

.sub-menu {
    border-bottom: 3px solid #424242;
}

input.search {
	background: #232323;
	color: #fff;
}

.filter-body {
    color: #fff;
    background: #232323;
}

.filter-footer {
    background: #333;
    border-top: 3px solid #ff6666;
}

.content-page {
    background: #232323;
    color: #f0f0f0;
}

.content-buttons>li {
    background: #333333;
    border-right: none;
    color: #fff;
}

.drop-menu-click, .content-buttons>li.drop-menu-click {
    background: #333333;
}

.hidden-menu {
    background: #424242;
}

.hidden-menu:before {
    border-bottom: 8px solid #424242;
}

.hidden-menu li {
    color: #f0f0f0;
    background: #333;
    border-bottom: 1px solid #424242;
}

.favourite-button {
    background: rgba(255, 103, 102, 0.5);
}

.content-main-info {
    border-top: 1px solid #424242;
}

.view-list {
    border: 1px solid #424242;
}

.view-list a.current {
    font-weight: 500;
    color: #989898;
}

.video-block-description {
    background: #333333;
}

.preview-block {
    background: #232323;
}

.preview-title {
    color: #ff6666;
}

.more-alt-names {
    background: #3cce7b;
}

.preview-categories a {
    color: #ff6666;
}

.update-list li:nth-child(odd)>a {
    background: #232323;
}

.update-list li:nth-child(even)>a {
    background: #333333;
}

.update-list a {
    color: #f0f0f0;
    border-top: 1px solid #1b1b1b;
}

.update-date {
    border-right: 1px solid #1b1b1b;
}


.accordion li {
    background: #333333;
    margin-bottom: 2px;
    border-left: 3px solid #ff6666;
}

.accordion-content {
    padding: 5px 5px 10px 10px;
    background: #424242;
}

.accordion .accordion li {
    background: #333;
}
  
.accordion-content:before {
    background: none;
}

.chosen-container-multi .chosen-choices {
    border: 1px solid #424242;
    background-image: linear-gradient(#333333 1%,#333333 15%);
}

.chosen-container .chosen-drop {
    border: none;
    background: #333;
}

.chosen-container-multi .chosen-choices li.search-choice {
    border: none;
    background: #232323;
    box-shadow: none;
    color: #f0f0f0;
}

.chosen-container .chosen-results li.no-results {
    color: #f0f0f0;
    background: #333333;
}

.chosen-container .chosen-results li.highlighted {
    background-color: #111
}

.chosen-container .chosen-results {
    color: #f0f0f0;
}

input[type="text"], input[type="password"], select {
    color: #f0f0f0;
    background: #333333;
    border: 1px solid #424242;
}

input[type="submit"].search-submit {
    background: url(https://getfile.dokpub.com/yandex/get/https://yadi.sk/i/FAqlalbQ3KF3y7) no-repeat;
}

.pagination span, .pagination a {
    color: #8c8c8c;
}

.admachina-banner {
    height: 0!important;
}


textarea {
    border: 1px solid #424242;
    background: #333;
}

.default-button {
    background: #333;
    color: #f0f0f0;
    border: 1px solid #424242;
}

.comments-block {
    color: #8e8e8e;
}

.comment {
    border-bottom: 1px solid #424242;
}

.reply-button {
    color: #f0f0f0;
    background: #333;
}

.jStar {
    background:url("https://getfile.dokpub.com/yandex/get/https://yadi.sk/i/DXVhiTdj3KF3mE") repeat-x !important
}

.rating {
    background: #111;
}

.jRatingColor {
    background-color: #ff6766;
}

.jRatingAverage {
    background-color: #fbff00;
}

.pluso-wrap {
    background: #232323;
}

table {
    background-color: #333;
}

.profile-filter {
    background: #333333;
}

.live-search-wrapper {
    background: #333;
    border: 1px solid #3cce7b;
}

.live-search-wrapper a {
    color: #fff;
    border-bottom: 1px solid #424242;
}
}

@-moz-document url-prefix("http://yummyanime.club/posts") {
.post-title {
    color: #ffffff;
}

.content-page {
    color: #c5c5c5;
}
}

@-moz-document url-prefix("http://yummyanime.club/profile") {
.list-with-icons a:hover, .list-with-icons a.active {
    background: #333333;
}

.list-with-icons a {
    color: #fbfbfb;
    border-bottom: none;
}

.list-with-icons {
    border: 1px solid #333333;
}

.list-buttons button, .list-buttons a, .list-button {
    border-left: none;
    border: 1px solid #232323;
}

/* Notices */
    
.notif-list li {
    border-bottom: 1px solid #333333;
}


/* Profile */

.avatar img {
    box-shadow: none;
    border: 1px solid #333333;
}

.inner-block {
    background: #232323;
}

.profile-filter {
    background: #333333;
}

.tips-header:active, .tips-header.active {
    box-shadow: inset 0 0 200px #f66;
}

.tips-content {
    background: #232323;
    border: 1px solid #333333;
}

.chosen-container-single .chosen-single span {
    color: #fd6863;
}

.chosen-container-single .chosen-single {
    border: 1px solid #333333;
    background-color: #2d2d2d;
    background: #2d2d2d;
}


/* Friends */

.main-button.neutral {
    box-shadow: none;
    border: 1px solid #333333;
    color: #ff6766;
}


/* Edit */

.content-page img {
    box-shadow: none;
}
}

@-moz-document url-prefix("http://yummyanime.club/profile/notices") {
.list-buttons button, .list-buttons a, .list-button {
    border: none;
}
}

@-moz-document url-prefix("http://yummyanime.club/about-yummy") {
.team-block {
    color: #fff;
}

.team-block:hover .team-img {
    box-shadow: 0 0 0 40px #424242;
}
}

@-moz-document url-prefix("http://yummyanime.club/catalog") {
.category-list {
    border: 1px solid #232323;
}

.category-list>li>a {
    border-bottom: none;
}

.category-list a {
    color: #fff;
}

.category-list>li:nth-child(odd) {
    background: #333333;
}

.category-list-inner {
    background: #424242;
    border-bottom: none;
}
}

