css:
@-moz-document domain("insanejournal.com") {
/* MUST BE USED WITH MONOCHROMATIC LEFT SIDEBAR THEME */

/* LEFT SIDEBAR: BACKGROUND COLOR */

#IJ-sitepage-nav,
#IJ-sitepage-body,
#IJ-sitepage-userpic,
#header-nonmenu {
    background-color: #DEEDDE !important;
}

/* BORDER TO THE RIGHT OF USERPIC */

#IJ-sitepage-userpic {
    border-right: 1px solid #C2D6C2 !important;
}
/* LEFT SIDEBAR: LINKS */

#IJ-sitepage-nav a,
#IJ-sitepage-userpic a a:visited,
#header-nonmenu a a:visited {
    color: #527A52 !important;
}
/* LEFT SIDEBAR: LINKS HOVER */

#IJ-sitepage-nav a:hover,
#IJ-sitepage-userpic a:hover,
#header-nonmenu a:hover {
    color: #669966 !important;
    text-decoration: none;
}
/* LEFT SIDEBAR: HEADINGS */

.notlink {
    color: #000000 !important;
}
/* MAIN BODY BORDER */

.sidebar #IJ-sitepage-content,
.sidebar #IJ-sitepage-footer {
    border-left: 1px solid #C2D6D2;
}
/* TOP NAV: MY IJ, POST, ETC. */

#IJ-sitepage-freqlinks {
    background-color: #f0f5f0 !important;
    border: 1px solid #C2D6C2 !important;
}
#IJ-sitepage-freqlinks a:hover {
    background-color: #E7E0EB !important;
    color: #669966 !important;
    text-decoration: underline;
}
/* IJ PAGES: GENERAL STYLING */

#IJ-smallfont td {
    font-family: arial, helvetica;
    font-size: 14px;
}
#IJ-sitepage-content a:link,
#IJ-sitepage-footer a:link {
    text-decoration: none !important;
    color: #527A52 !important;
}

#IJ-sitepage-content a:hover,
#IJ-sitepage-footer a:hover {
    text-decoration: none !important;
    color: #669966 !important;
}

#IJ-sitepage-content a:visited,
#IJ-sitepage-footer a:visited {
    text-decoration: none !important;
    color: #3D8F3D !important;
}

#IJ-sitepage-footer {
    border-right: 0px solid #C2D6D2 !important;
}

#IJ-sitepage a { color: #527A52; text-decoration: none; }

/* PAGE DIVIDERS */
hr { border-top: 1px solid #eeeeee; }
}
