css:
@-moz-document domain("bitmarket.pl"), domain("bitmarket.net") {
body {
    background: #111 url(http://i62.tinypic.com/2a2eir.png);
    color: #fff;
}
image[x="140"] {
    display: none;
}
.grid {
    stroke: #222 !important;
}
.volume {
    fill: hsla(210, 92%, 30%, 1) !important;
    stroke: hsla(210, 100%, 20%, 1) !important;
}
.minmax {
    fill: #fff !important;
    stroke: #fff !important;
}
.b-modal {
    background: #000 !important;
}
.b-popup {
    background: #222;
    color: #fff;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #fff;
}
header {
    background: transparent;
}
.navbar-inner {
    background: #222;
}
.top-bar-market-rate {
    background: #333;
}
.navbar {
    border-color: rgb(65, 187, 25) !important;
}
.navbar .btn {
    color: #000;
}
.navbar .nav > li > a,
.navbar .nav > li > a:visited {
    color: #fff !important;
}
.navbar .nav > li > a:hover {
    background: #2a2a2a !important;
}
.btn:hover,
.btn:focus {
    color: #fff;
    background: #000;
}
footer {
    background: #222;
    color: #ccc;
}
footer a {
    color: #888;
}
footer a:hover {
    color: #bbb;
}
.logo {
    filter: invert(1) hue-rotate(180deg) brightness(1.7) saturate(70%);
}
text {
    stroke: #ddd !important;
    fill: #fff;
}
#graph1 {
    background: #000;
}
.tooltip {
    stroke: #222 !important;
    fill: #222 !important;
}
.legend {
    color: #fafafa !important;
}
input {
    background: #111 !important;
    color: #fff !important;
    border-color: rgb(65, 187, 25) !important;
}
.add-on {
    background: #222 !important;
    text-shadow: none !important;
    border-color: rgb(65, 187, 25) !important;
}
header a {
    color: #fff !important;
}
.lrform {
    background: #1a1a1a;
}
.fwidget h6 {
    color: #aaa;
}
polyline.bid {
    fill: hsla(211, 68%, 44%, 1) !important;
}
polyline.ask {
    fill: hsla(85, 88%, 37%, 1) !important;
}
#market_sell .my,
#market_buy .my {
    color: rgb(65, 187, 25);
}
.white-section {
    background: #333;
}
}
