css:
@-moz-document domain('weibo.com') {
.PCD_mplayer,
  #v6_pl_content_biztips,
  #v6_pl_rightmod_updatev6,
  #v6_pl_rightmod_ads35,
  #v6_pl_rightmod_noticeboard,
  #v6_trustPagelet_recom_member,
  #v6_pl_rightmod_recominfo,
  #v6_pl_rightmod_ads36,
  #v6_pl_rightmod_updatev6 ~ div[style*="position: fixed;"],
  .WB_feed div[node-type="feed_spread"],
  .WB_feed .W_tips.tips_warn,
  .WB_frame.WB_frame_bottom,
  .footer_link,
  #v6_pl_rightmod_friends,
  #v6_pl_rightmod_recomfriends,
  #v6_pl_rightmod_groups,
  #v6_pl_rightmod_helpat,
  #v6_pl_rightmod_feedback,
  #v6_pl_rightmod_helplike,
  #v6_pl_rightmod_helpcomment,
  #v6_pl_rightmod_helpfav,
  #v6_pl_rightmod_rank,
  #v6_pl_rightmod_attfeed,
  #Pl_Core_ThirdHtmlData__6,
  #Pl_Core_RecommendFeed__75 ~ div{
    display:none;
  }
  div[title="我也想出现在这里"],
  div[title="我也想出现在这里"] ~ div,
  div[title="我也想出现在这里"] ~ div ~ div,
  div[feedtype="ad"],
  .WB_right_module,
  #v6_pl_rightmod_hongbaoentrance,
  #v6_TrustPagelet_Recom_MusicRank,
  .WB_ad_tm2015 {
    display:none;
  }
  /*left menu */
  .WB_left_nav .lev_Box:nth-of-type(5),
  .lev_line_v2 {
    display:none;
  }
  /* 侧边栏 */
  #v6_pl_rightmod_hongbao, #pl_common_ali {display: none;}

  /*header*/
  .WB_global_nav .gn_header{width:auto;}
  .WB_global_nav .gn_nav_list li .gn_name .S_txt1{
    max-width: 200px;
  }
  /*body*/
  .WB_frame{
    width:100%;
  }
  .B_discover .WB_frame{
    width:auto!important;
  }
  .WB_main_l{
    width:240px
  }
  .WB_main_c {
    width:calc(100% - 10px)!important;
  }
  .WB_frame .WB_left_nav{
    width:auto;
    margin-top:175px;
  }
  .WB_frame #plc_main{
    width:calc(100% - 240px)!important;
  }
  .B_message .WB_main_c{
    width:calc(100% - 250px)!important;
  }
  .B_page #plc_main{
    width:calc(100%)!important;
  }
  .WB_frame_c{
    width:calc(100% - 460px)!important;
  }
  .S_main_bg{
    width: auto;
    margin-left:10px;
  }
  .S_content_l{
    width:calc(100% - 310px);
  }
  .private_dialogue_box .private_dialogue_body{
    width:100%;
  }
  .private_list_box .private_bubble_body .bubble_cont{
    width:1000px;
    max-width: 100%;
  }
  .private_list_box .private_bubble_body .bubble_cont .cont_font{
    width: auto;
    max-width: 100%;
  }
   /*text*/
   .WB_text{
     line-height:1.5!important;
   }
  /*timeline*/
  .WB_timeline{
    left:auto;
    right:0px;
    width:130px;
    font-size:18px;
    line-height:1.5;
  }
  .WB_timeline ul li a{
    display:block;
    width:auto;
  }
  .WB_timeline .block li .current span{
    font-size:16px;
  }
  .WB_timeline ul li.last a{
    width:auto;
  }
  /*post*/
  #v6_pl_content_publishertop{
    margin-right:0;
  }
  .send_weibo .key{
    display:none;
  }
  .WB_notes{
    margin-top:0;
  }
  .send_weibo .kind{
    width:460px;
  }
  /*person*/
  #v6_pl_rightmod_myinfo{
    position:fixed;
    left:0;
    top:66px;
    border-radius:0;
  }
  .PCD_header .banner_transition{
    background-size:cover
  }
  /*nav*/
  .WB_left_nav .lev_Box h3.lev a{
    font-size:18px;
  }
  /*search*/
  .WB_search_s .W_input{
    width:200px;
  }
  /*gotop*/
  .W_gotop{
    left:auto;
    right:0;
  }
  .B_artical .W_gotop_v3{
    left:50%;
  }
  /*article*/
  .WB_editor_iframe{
    height:auto!important;
    overflow:auto!important;
    margin-bottom:0!important;
  }
  div[node-type="maskContent"]{
    display:none;
  }
  /*font*/
  @media screen and (min-resolution: 192dpi) {
    body{
      font-size:16px;
    }
    .WB_frame_a, .WB_frame_a_fix, .WB_frame_b, .WB_frame_c, .WB_frame_d, .WB_main_l, .WB_main_c, .WB_main_r, .WB_main_cr, .WB_main_a, .WB_toptips{
      font-size:18px;
    }
    .send_weibo .func .limits{
      margin-top: 2px;
    }
    .W_f14{
      font-size:20px;
      line-height: 30px;
    }
    .WB_global_nav .gn_nav_list li{
      font-size:20px;
    }
    .WB_left_nav .lev a{
      font-size:16px;
    }
    .PCD_pictext_h .text_mod_tit{
      line-height:1.5!important
    }
    .WB_editor_iframe{
      font-size: 20px;
    }
    .feed_list .content{
      font-size: 20px;
      line-height: 32px;
    }
  }
  /*bpage*/
  .WB_frame_a{
    width:auto;
    margin-right:0;
  }
  .B_page .WB_frame{
    width:100%;
  }
  .Tf_text{
    line-height: 1.5;
  }
}
