css:
@-moz-document regexp('/*[[yourdomain]]*/') {

}

@-moz-document regexp('^https?:///*[[yourdomain]]*/.*') {

body {
		color: #dddddd;
    }

	html {
        background-color: #1e2122 !important;
    }

    .snackbar {
        background-color: #272b2c;
		color: #dddddd;
    }

	.folder-layout .folder-container .folders-pane {
        background-color: #272b2c;
	}

    .folder-layout .folder, .folder-layout .file, .folder-layout .back-button, .browser-file-list .folder, .browser-file-list .file, .browser-file-list .back-button, .browser-folder-list .folder, .browser-folder-list .file, .browser-folder-list .back-button {
        border-bottom: 1px solid #1e2122;
    }

    #fanart-overlay {
        background-color: #272b2c;
    }

    #do-search:before, .options-search-wrapper:before {
        color: #dddddd;
    }

    #remote-background {
        background-color: #1e2122;
    }

    .options-search {
        background-color: #202324 !important;
        color: #dddddd !important;
    }

    .deselect-all{
        color: #dddddd !important;
    }

    #search {
        background-color: #2b2f30 !important;
		color: #dddddd;
    }

    #shell {
        background-color: #1e2122 !important;
    }

    .set {
        background-color: #191b1c !important;
    }

    .set__header {
        background-color: #202324 !important;
    }

    .set__title {
        color: #dddddd;
    }

    .card-grid--tall .card, .card-grid--episode .card, .card-grid--square .card, .card-artist .meta, .card-grid--tall .card .artwork {
        background: #242829 !important;
        background-color: #242829 !important;
    }
    .card-grid--tall .card .title a, .card-grid--episode .card .title a, .card-grid--square .card .title a, .card-artist .meta .title a {
        color: #d6d6d6 !important;
    }

    .card-grid--wide .card {
        background-color: #242829;
    }

    .card-grid--wide .card .artwork .thumb:after {
        background: rgba(255,255,255,0.55);
    }

    .controls-primary {
        background-color: #1e2122 !important;
    }

    nav {
        background-color: #1e2122 !important;
    }

    nav i:before {
        color: #dddddd;
    }

    nav i:after {
        color: black !important;
    }

    .region-first {
        background-color: #272b2c !important;
    }

    .active-player-kodi #logo, .active-player-kodi .playlist-header .player-toggle li.kodi, .region-first-primary .nav-sub .items li a.active {
        color: #298eb0 !important;
    }

    .btn-flat-play {
        background-color: #298eb0 !important;
    }

    .set-name a {
        color: #298eb0 !important;
    }

    .set-name a:hover {
        color: #1c9bc6 !important;
    }

    .region-first li a{
        color: #dddddd !important;
    }

    div.track, .song-artist a, .cell-last{
        color: #dddddd !important;
    }

    ul.actions:before {
        color: black;
    }

    .filters-container .filter-btn.filterable-add, .local-playlist-list .region-first .new-list{
        background-color: hsl(210, 2%, 25%);
    }

    .filters-active-bar {
        background-color: #1b1c1d;
    }

    .filters-active-all {
        color: #dddddd !important;
    }

    .filters-active-all:after{
        background-image: none !important;
    }

    .filters-active-bar .remove {
        color: #dddddd !important;
        opacity: 0.9;
    }

    .selection-list li span{
        color: #dddddd !important;
    }

    li:after, div.title:after, div.subtitle:after, a:after, span.crop:after{
        background-image:none !important;
    }

    i.mdi:before{
        color: #c0c0c0;
    }

    .region-content {
        background-color: #222526 !important;
        color: #dddddd;
    }

    #nav-bar nav ul li:hover a {
        background: #2e7187 !important;
        color: #dddddd !important;
    }

    .card-grid--square .card .artwork {
        background: #1e2122 50% 50% no-repeat;
    }

    div.artwork a[style*="thumbnail_default.png"] {
        filter: invert(100%);
    }

    .playing-thumb[src*="thumbnail_default"]:not([style]) {
        filter: invert(90%);
    }

    .thumb[style*="/image/"] {
        filter: saturate(88%);
    }

    .card {
        border-radius: 0px;
    }

    .landing-page .landing-section, .region-content, .landing-set .set__collection, .landing-set .set__more {
        padding: 12px 12px 12px 12px;
    }

    .landing-content {
        filter: saturate(88%);
    }

    .region-content {
        filter: saturate(88%);
    }

    .songs-table tr.song.local-row-playing, .songs-table tr.song.local-row-paused, .songs-table tr.song.kodi-row-playing, .songs-table tr.song.kodi-row-paused {
        background-color: #272b2c !important;
    }

    .table-hover>tbody>tr:hover, .songs-table tr.song.menu-open, .songs-table tr.song:hover {
        background-color: #272b2c !important;
    }
    .mdi.play:before {
        color: #dddddd;
    }

    .playing-title a {
        color: #dddddd !important;
    }

    .page, .page-secondary, .api-method--execute, .help--page .region-content-wrapper .region-content, .settings-form {
        max-width: 58em;
        margin-left: 0;
        background: #272b2c;
    }

    .form-control {
        color: #dddddd;
    }

    .settings-form-draggable .form-groups .draggable-row:nth-child(even) {
        background: #1e2122;
    }

    .lab--items.page.page-secondary h2, .lab--items.page.page-secondary h3, .lab-item p {
        color: #dddddd;
    }

    .lab-item:hover {
        background: #1e2122;
    }

    a:hover, a:focus {
        color: #298eb0;
    }

	.player-menu-wrapper ul {
		background: #272b2c;
	}

	.player-menu-wrapper ul li{
		color: #dddddd;
	}

	.modal-content {
		background-color: #272b2c;                                    
	}

    .btn-default, #form-edit-add-another {
		background-color: #4a5254;
    }

	.btn.btn-primary, .form-control-wrapper .material-input:before {
		background-color: #298eb0;
	}

    .help--page .help--overview--report ul li:nth-child(even) {
		background-color: #272b2c;    
    }

	pre {
		background-color: #1e2122;
		color: #dddddd;
	}

	.help--page .region-content-wrapper pre {
		border: 1px dashed black;
	}

    .help--page .region-content-wrapper code {
		color: #dddddd;
		background-color: #1e2122
	}

    .layout-container.with-sidebar-first div.region-content > ul > li > code {
		color: #dddddd;
    }

    .layout-container.with-sidebar-first div.region-content h3 {
		color: #dfdfdf;
    }

    .playlist-header .playlist-menu .dropdown-menu {
        background: #272b2c;
    }

    .playlist-header .playlist-menu .dropdown-menu li a, .playlist-header .playlist-menu .dropdown-menu li.dropdown-header {
    	color: #dddddd;
    }

    .cell-label.song-album .crop {
		color: #dddddd;
    }

    #disconnected .message {
        background: #272b2c;
    }

    #disconnected .btn:hover {
        background: #2c9abf;
    }

	.local-playlist .local-playlist-header {
        background: #272b2c;
	}

    .local-playlist .local-playlist-header h2 {
		color: #dddddd;
    }

    .modal ul.options li:hover {
		background: rgba(52,57,59,0.7);
    }

    .songs-table tr.song a:hover {
		color: #dddddd;
    }

    .card-grid--musicvideo .card .artwork, .card-grid--episode .card .artwork {
        background: #1e2122 50% 50% no-repeat;
    }

	.edit-form .form-tabs, .form-imageselect__tabs {
        background: #272b2c;
	}

	.edit-form .form-tabs li.active, .form-imageselect__tabs li.active {
    	background: #1e2122;
	}

	.edit-form .form-control {
	    background: #1e2122;
		border-bottom-color: #16191a;
	}

	.edit-form .form-control:focus {
    	border-bottom-color: #1f2324;
    	background: #222526;
	}

}
