css:
@-moz-document domain("robertsspaceindustries.com") {
/*
 * Spectrum Font Edit v0.1 (06/25/2017)
*/

/*-------------------------------------------------------------------------------------------------
Sidebar MODIFICATIONS
---------------------------------------------------------------------------------------------------*/

.sidebar-list .sidebar-item>.column.content>a{
	font-size:19px;
}
.sidebar-list .sidebar-item>.column.content>a .text{
	color:#fff;
}

/*-------------------------------------------------------------------------------------------------
BreadCrumbs MODIFICATIONS
---------------------------------------------------------------------------------------------------*/


.breadcrumb .trail .breadcrumb-link{
	font-size:20px;
}


/*-------------------------------------------------------------------------------------------------
THREAD LIST MODIFICATIONS
---------------------------------------------------------------------------------------------------*/


/* Adds indiciation of a visited thread (dark theme) */
.theme-dark #page.forum-channel .row.thread:not(.unread) .column.subject .subject-content .thread-subject {
	color: #a0a0a0 !important;
}
.theme-dark #page.forum-channel .row .column.subject .subject-content a.thread-subject{
	color: #fff !important;
}
#page.forum-channel .row .column.subject .subject-content .thread-subject{
	font-size:1.2rem;
}

/*-------------------------------------------------------------------------------------------------
THREAD MODIFICATIONS
---------------------------------------------------------------------------------------------------*/

h1{
	font-size:1.8em !important;
 
}
.theme-dark .content-block.text, .theme-dark .content-block.text a{
    color:#fff;
}
.theme-dark .public-DraftEditor-content blockquote, .theme-dark blockquote,
.theme-dark .content-block.quote .content-blocks .content-block.text{
 	color:#d2d8e0;
}
.forum-thread-item>.content .content-blocks,
.forum-thread-item.style-type-small .content-block.text,
.content-block.quote .content-blocks .content-block.text{
	font-size:1.2rem;
}
.forum-thread-item .action{
	font-size:.9rem;
}

/*-------------------------------------------------------------------------------------------------
Message
---------------------------------------------------------------------------------------------------*/

.message-item>.content>.bottom .body{
 	font-size:16px;
    color:#fff !important;
}
}
