css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://144.217.161.171/"), url("http://www.s0urce.io/"), url("http://s0urce.io/") {
::-webkit-scrollbar { width: 12px; } /* Track */ 
  ::-webkit-scrollbar-track { 
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3) !important; 
	-webkit-border-radius: 10px !important; 
    border-radius: 10px !important; 
    border: 1px solid #FFFFFF !important
  }  
  
  ::-webkit-scrollbar-thumb { 
    -webkit-border-radius: 10px !important; 
    border-radius: 10px !important; 
    background: rgba(255,255,255,1) !important; 
  }
  
  ::-webkit-scrollbar-thumb:window-inactive { 
    background: rgba(255,0,0,0.4); 
  }
  
  a:active, a:link, a:visited, body, html {
    color: #FFFFFF;
  }
  body, html{
    background-image: url(https://static.decalgirl.com/assets/designs/large/blackwood.jpg),radial-gradient(at top,#273541,#0b0b0c) !important;
  }
  .window-title {
    background-color: #202020 !important
  }
  .window, .window-fixed {
    border-color: #202020 !important;
    border-left: 1px solid #202020 !important;
    border-right: 1px solid #202020 !important;
    border-bottom: 1px solid #202020 !important;
    background-color: #343434 !important;
    border-radius: 6px !important;
    overflow:hidden !important;
  }
  .window-content, .window-content > div{
    background-color: #343434 !important;
  }
  
  .rank-popup {
    border-color: #202020 !important;
  }
  .list-table-rank-content {
    background-color: #FFFFFF;
  }
  .desktop-element-img, .shop-firewall-icon, .window-other-message-tile, .window-title, .window-bt-icon {
    filter: brightness(200%)
  }
  .window-firewall-wrapper {
    background-image: url("http://i.imgur.com/MZuYG0K.png") !important
  }
  .window-firewall-part-wrapper {
    background-image: url("http://i.imgur.com/zUPYSq4.png") !important
  }
  .window-firewall-part {
    border: 2px solid #d24f4f !important;
  }
  .window-firewall-amount {
    background-color: #d24f4f !important
  }
  .window-firewall-letter {
    color: #FFF !important;
    text-shadow: 0px 0px 0;
  }
  #window-firewall-base > .window-firewall-part {
    height: 35px !important;
    width: 35px !important;
  }
  #window-firewall-base > .window-firewall-part > .window-firewall-letter {
    padding-top: 4px !important;
  }
}
