css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.meneame.net") {

	.ads-interlinks, 
	#ads-Right1, 
	#ads-Right2, 
	#toplink, 
	#cookieChoiceInfo, 
	#banner_right, 
	#widget-official-subs-slider, 
	#backTop,
	#sidebar > div[style="width:300px;height:250px;margin-bottom:25px"], 
	#sidebar > div[style="width:300px;margin-bottom:25px"], 
	div.news-body div[style="margin-top:10px;max-width: 980px;overflow: hidden !important;"],
	#newswrap div[style="margin:20px auto;width:320px;height:50px;white-space:nowrap;overflow:hidden"],
	#header-logo, 
	button.social-share, 
	div.news-summary div.clics, 
	div.news-summary div.warn, 
	div.auth-buttons, 
	h2 span.wideonly, 
	.show-responses, 
	#comments-top div.topbox > div,
	#header .header-center-wrapper, 
	#footwrap, 
	#footthingy,
	div.sidebox-rounded.orange .body .finished,
	div.pages span.nextprev,
	div.commentform.warn,
	div.topbox a:first-child,
	#singlewrap section .social-buttons,
	#singlewrap section h1,
	#singlewrap section p.intro,
	#singlewrap section div.separator,
	#singlewrap section form div.legend,
	.news-details-data-up,
	#widget-popular,
	button.favorite,
	div.news-summary + div:not([class]),
	#sidebar > div:not([class]),
	#sidebar > a[target="_blank"],
	div.news-body > div:not([class]) {
		display: none !important;
	}

	/* div.sidebox:first-child 
	#sidebar div.sidebox.red, #sidebar div.sidebox.red, #sidebar div.sidebox:last-child, #sidebar div.sidebox:nth-child(5) {
		display: none !important;
	}
	*/
	div.news-summary {
    padding: 10px 0 0px 0 !important;
	}
	div.news-summary div.votes {
		visibility: hidden;
		height: 30px;
		margin: 0;
		padding: 0;
	}
	div.news-summary div.votes a {
		visibility: visible;
		pointer-events: none;
		cursor: default;
		color: black;
		margin-top: 6px;
	}
	
	div.news-body > div:first-child {
		background-color: silver !important;
	}
	
	div.news-body {
    margin-bottom: 10px;
	}
	
	#header {
		margin-top:0px;
	}
	div.header-top-wrapper {
		position:static !important;
	}

	a.sub-name {
		border-left:none !important;
		padding-left: 0px !important;
		margin-left: 8px !important;
	}
	
	.header-top-wrapper, div.menealo a, div.menealo span {
    background-color: black !important;
		background-image: none;
		border: solid 1px black !important;
	}
	div.news-details a.comments {
		background-color: white !important;
		color: black;
		font-weight: unset;
	}
	
	div.sidebox div.votes span, div.sidebox div.clics span {
		color: black !important;
		border: 2px solid black !important;
	}
	
	div.sidebox div.header, div.sidebox div.header a {
		background-color: white !important;
		color: black !important;
	}
	div.pages a, div.pages span {
		background-color: white !important;
		color: black !important;
	}
	div.pages a {
		border: solid 1px black !important;
	}
	div.pages span.current {
		background-color: black !important;
		color: white !important;
		border: solid 1px black !important;
	}
	div.sidebox-rounded.orange .header {
		background-color: white !important;
		color: black !important;
		border-top-left-radius: 8px;
		border-top-right-radius: 8px;
		border: 1px solid #dadada;
	}
	div.sidebox-rounded.orange .header a {
		color: black !important;
	}
	div.sidebox-rounded.orange .body  {
    padding-top: 10px;
	}
	.sidebox-rounded .guest {
		display: inline-block;
	}
	.news-details-data {
		display: inline-block;
		width: auto !important;
	}
	.news-details-main {
		display: inline-block;
		width: auto !important;
		white-space: nowrap;
		float: left;
	}
	.news-details-data-down {
		display: inline !important;
	}
	div.comment div.comment-body {
		background-color: #ECECEC;
	}
	div.comment.high div.comment-body {
		background-color: gainsboro;
	}
	div.comment.author div.comment-body {
		background-color: #EBEDF4;
	}
	div.comment.author.high div.comment-body {
		background-color: #CAD0E2;
	}
	#mDialog_box {
	 user-select: auto !important;
		-moz-user-select: auto !important;
		-webkit-user-select: auto !important;
  }
	#mDialog_content {
    height: auto;
	}
	div.topbox {
		border: none;
	}

}
