css:
@-moz-document url-prefix("https://www.unibet.eu") {
.KambiBC-event-page-component__columns {
    width: 100%!important;
    max-width: 100%!important;
    
 	display: -webkit-box;
    display: -moz-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    
    flex-direction: column!important;
}

.KambiBC-has-widgets {
	display: none!important; 
}

#KambiBC-container .KambiBC-event-page-component__column--1, #KambiBC-container .KambiBC-event-page-component__column--2 {
    width: 100%!important;
    max-width: 100%!important;
}

/* Betting container */
#KambiBC-container .KambiBC-event-page-component__column--1, #KambiBC-container .KambiBC-event-page-component__column--2 {
	order: 2!important;
}

/* Main stream container */
#KambiBC-container .KambiBC-event-page-component__column--2 {
   	order: 1!important;
}


.KambiBC-event-page-component__cinematic.KambiBC-js-event-page-component-cinematic {
    
}
/* Remove margin from score container */
#KambiBC-container .KambiBC-event-page-component--multi-column[data-eq-state~="2columnsLarge"] .KambiBC-event-page-component__column .KambiBC-event-page-component__scoreboard-container, #KambiBC-container .KambiBC-event-page-component--multi-column[data-eq-state~="2columnsLarge"] .KambiBC-event-page-component__column .KambiBC-event-page-component__visualisation-scoreboard {
    margin-bottom: 0px!important;
}

/* Player */
.KambiBC-video-stream-player {
    height: 0!important;
    padding-bottom: 56.25%!important;
    position: relative!important;
}
.KambiBC-player, .KambiBC-player--flash, .KambiBC-video-stream-player__play-btn  {
    max-width: 100%!important;
    max-height: 100%!important;
    height: 100%!important;
    position: absolute!important;
    top: 0!important;
    bottom: 0!important;
    left: 0!important;
    right: 0!important;
}
}
