css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".*redmine.*"){    
	html{
 	overflow: hidden;
	position: fixed;/*relative*/
	display:block;
	margin: 0 !important;
	padding: 0 !important;
	height: 100% !important;
	min-height: 100% !important;
	background: #333 !important; /*jet*/
	/*border: 1px dotted white;*/
}


body {
    color: #fff;
    overflow-y:hidden;
	display:block;
	background: #333 !important; /*jet*/
	
	position: fixed;/*relative*/
	margin: 0 !important;
	padding: 0 !important;
	height: 100% !important;
	min-height: 100% !important; /**/
}

#wrapper {
	display:block;
	overflow:hidden;
    position: fixed;/*relative*/
	margin: 0 !important;
	padding: 0 !important;
	height: 100% !important;
	min-height: 100% !important;
	background: #333 !important; /*jet*/

}

#wrapper2 {
	display:block;
	overflow:hidden;
    position: fixed;/*relative*/
	margin: 0 !important;
	padding: 0 !important;
	height: 100% !important;
	min-height: 100% !important;
	background: #333 !important; /*jet*/

}
#wrapper3 {
	display:block;
	overflow:hidden;
    position: fixed;/*relative*/
	margin: 0 !important;
	padding: 0 !important;
	height: 100% !important;
	min-height: 100% !important;
	background: red;

}

#ajax-indicator {
    position: absolute;
    background-color: #C8F7C5;
    border: 1px dotted #e8eaec;
    top: 1px;
    left: 1px;
    width: 100%;
    height: 100%;
    text-align: center;
    /* padding: 16.6em; */
    opacity: 0.3;
    line-height: 750px;
    color: #fff;
    text-align: center;
    line-height: 750px;
}

a, a:link, a:visited {
	color: #C8F7C5;
    text-decoration: underline;
}

#main {
	
	margin-top:81px;
	font: 100% Tahoma, Verdana, Arial,sans-serif;
	/*--------------------*/
	display:block;
	overflow:hidden;
	position: inherit;
	height: 100% !important;
	min-height: 100% !important;
	width: 100% !important;
	min-width: 100% !important;
	background:#222;
}

#header  {  
	display:block;
	overflow:hidden;
    position: fixed;/*relative*/
	padding: 0 !important;
	height: 80px !important;
	min-height: 80px !important;	
	margin-top: auto;
	z-index: 999; 
	width: 100% !important;
	min-width: 100% !important;
	background: #444;
	border-bottom: 1px #636363 dotted;
	}

#sidebar {
	/*display:none;*/
	width: 10%;
}

#content {

	/*
   background: #1D1E18;*/
    background:#444;
    overflow-x: hidden; /*for hiding horizontal scroll bar*/
    overflow-y: auto; /*for vertical scroll bar*/
	
	margin: 0 !important;
	padding: 6px 10px 10px 10px;
	/*--------------------*/
	display:block;

	height: 90% !important;
	min-height: 90% !important;
	width:88%;
	
}

#header a.mobile-toggle-button {
	min-width:100%;
}

#main.nosidebar #content {
    width: 99% !important;
	min-width:99% !important;

}


#main a {
 	color: #C8F7C5;   
}

#main a:hover {
    color: #444;
    text-decoration: underline;
    background-color: #C8F7C5;
}

#main-menu {
    background-color: #C8F7C5;}
#main-menu li a {
    background-color: inherit;
	color:#444;}
#main-menu li a.selected, #main-menu li a.selected:hover {
    background-color: #444;}
#main-menu li a:hover {
    background-color: #333;}

div.issue {
	background: #555;
}

h2, h3, h4, .wiki h1, .wiki h2, .wiki h3 {
    border-bottom: 0px;
    color: #D7D9D7;
    font-family: Tahoma, Verdana, Arial,sans-serif;
}

h2, .wiki h1 {
    letter-spacing: -0.7pt;
}
#top-menu, #footer {display:none;}






#content h3 {
    font-weight: inherit;
    color: #C8F7C5;
}


div#issue-changesets {
    background: #333;
    color: #fff;
    font-size: 90%;
	border:1px dotted #666;
}

#issue-changesets div.changeset {    padding: 0px;}
#issue-changesets div a{
	color: #fff;
    font-weight: normal;
}
div#issue-changesets p {
    margin-bottom: 0.4em;
}

#issue-changesets h3 {
    font-weight: normal;
    font: 150% Tahoma, Verdana, Arial,sans-serif;
}


/**linha selecionada*/
.context-menu-selection {
	background-color: #00b90c;
	color:red;
}
.context-menu-selection a {
	color:red;
}


.context-menu-selection:hover {
    background-color: #333 !important;}

table.list tbody tr:hover{
    background-color: #333;

}

.box {
    background-color: transparent;
    color: #c7c7c7;
    line-height: 1.2em;
    border: 0.1em solid rgba(255, 255, 255, 0.16);
    border-radius: 3px;
}
#main a.user.active{
	font-weight:normal;
}



#login-form table {

    border: 1px solid #444;
    background-color: #555;}


div.flash.error, #errorExplanation {
     
	  background: url(https://cdn2.iconfinder.com/data/icons/new-year-s-hand-drawn-sticker/64/exclamation_point-128.png);
    background-size: 20px 20px;
	  background-repeat: no-repeat;
	  background-position:10px 2px;
	  border: none;
    color: #fff;
	  background-color: #FF2800; 
}


/**tabela*/
table.progress td.closed {
    background: #5bdb2c none repeat scroll 0%;
}
table.progress td.done {
    background: rgb(167, 236, 142) none repeat scroll 0%;
	
}

table.list th, .table-list-header {
	background: #C8F7C5;
	color:#333;
}

table.cal thead th, table.cal td.week-number {
	background:transparent;
}

#content th a {
	color:#444;
}

#content tbody a {
	font-weight:normal;
}

.odd {
    background-color: transparent;

}
.even {
    background-color: #3E3E3E;
}

/**fim tabela*/


div.tooltip:hover span.tip {
	background-color:#333;
	color: #fff;
}

table.cal td.today {
    background: rgba(200, 247, 197, 0.7);
}

table.cal td.today p.day-num {
    color:#333;
}


input, select {
	    border-radius: 4px;

  padding-left: 5px;
	-webkit-appearance: none;
	-moz-appearance: none;
   	appearance: none;
  box-shadow: none;
  border: 1px dotted #666;
  background: #333;
  color: #fff;
  text-decoration: none;
}

input[type=text]:focus, input[type=date]:focus,  input[type=datetime]:focus, input[type=number]:focus,
input[type=search]:focus,
input[type=time]:focus,
input[type=url]:focus,
input[type=email]:focus,
textarea:focus, 
select:focus{
	-moz-box-shadow: 0 0 8px #c8f7c5;
    -webkit-box-shadow: 0 0 8px #c8f7c5;
    box-shadow: 0 0 8px #c8f7c5;
 }







legend {
    color: #fff;
}




/*-----------------------------scrollbar*/

::-webkit-scrollbar {
  width: 10px;
  height: 10px;
}
 
::-webkit-scrollbar {
    width: 10px;
    background-color: #5f5f5f;
}


::-webkit-scrollbar-thumb {
    background-color: rgb(68, 68, 68);
    background-image: -webkit-gradient(linear, 40% 0%, 75% 84%, from(rgb(77, 156, 65)), color-stop(0.6, rgb(84, 222, 93)), to(rgb(25, 145, 29)));
    border-radius: 10px;
}








.gantt_hdr {
border: 1px dotted #444;
	border-left: 1px dotted #444; 
	background: #333;
}
	
}
