css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fdtpro.com") {
  body {
      background: #3c3f41;
      overflow: auto;
      color: #BBB;
  }

    a {
        color: #3e86a0;
    }
    
  /* Current Tab */

  /* Inactive Tabs */

  img.pointer {
      padding: 20px;
  }
    
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > img, body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > img:nth-child(1) {
        display: none;
    }
    
    #loadmask {
        position: fixed;
        top:0;
        left: 0;
    }

  input[type=button], input[type=submit], input[type=reset] {
      background-color: #4a8c53;
      font-weight: bold;
      border-radius: 5px;
      box-shadow: 0px 0px 0px 1px #2b2b2b inset;
      border: 1px solid #4a8c53;
      color: white;
      cursor: pointer;
      margin: 4px 2px;
      padding: 8px 13px;
      text-decoration: none;
  }

  input[type=button][disabled="true"] {
      cursor: not-allowed ;
      opacity: 0.3;
  }

  input[type=text], input[type=password] {
      box-sizing: border-box;
      margin: 3px 0;
      padding: 3px 5px;
      width: 75%;
  }

  select {
      background-color: #f6f6f6;
      border: none;
      border-radius: 4px;
      padding: 3px 5px;
      width: 100%;
  }

  td {
      text-align: center;
  }

  td.basic, td.d_l {
      background: white;
      border: 1px solid #CACACB ;
  }

  td.core {
      background-color: #f6f6f6;
      background-image: none;
      border-radius: 4px;
      padding: 20px;
  }

  td.dark, td.l_d {
      background: none;
      background-color: #474747;
      border-color: #474747;
  }

  td.darktitle, td.l_d_t {
      background: #E2E2E2 ;
      border: 1px solid #CACACB ;
      color: #474747 ;
  }

  td.grey_fds {
      background: none;
      background-color: #f6f6f6;
      border-color: #474747;
  }

  td.menu {
      padding-bottom: 10px;
  }

  td.menu_cur {
      padding-bottom: 10px;
  }
    
    a.menu_cur {
        color: #FFF;
    }

  td.read_only {
      background: #ffffff;
      border-color: #CACACB;
  }

  td.reverse, td.l_m {
      background: #E2E2E2 ;
      border: 1px solid #CACACB ;
      color: #474747 ;
  }

  td.tl_firsttab, td.inr, td.menu_cur, td.vert_plain, td.inl, td.tl, td.t, td.tr, td.l, td.sect, td.r, td.bl, td.b, td.br {
      background: #2b2b2b;
  }

  td.tld, td.ld {
      border-left: 1px solid white;
  }

  td.tld, td.td, td.trd, td.ld, td.menu, td.rd {
      background: #474747;
  }

  td.trd, td.rd {
      border-right: 1px solid white;
  }
}
