@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("amoursucre.com"), domain("amordoce.com"), domain("sweetamoris.de"), domain("corazondemelon.es"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com") {
   
    index > .connected {
       background-color : #FFFFFF !important;
       /*Fondo Base*/
       background-image : url("http://img05.deviantart.net/67a1/i/2017/179/e/0/base_by_darknessblack-dbec42r.jpg") !important ;
       background-repeat : no-repeat !important ;
       background-position : center center !important ;
       background-attachment : fixed !important ;
       background-size : cover !important ;
    }    
        #site-logo > a > img[src*="/logo-as.png"] {
           height : 0 !important ;
           width : 0 !important ;
           /*Logo*/
           background: url("http://orig06.deviantart.net/ec36/f/2017/179/e/7/logo_by_darknessblack-dbec6fs.png") no-repeat !important ;
 padding-left : 427px !important ;
           padding-top : 142px !important ;   
             
        }
       .halo {background-image : url("http://img15.hostingpics.net/pics/250980SansHalo.png") !important ;}
       /**********************************************PARTE SUPERIOR************************************************************/
  		/*Fondo de la tarjeta*/
      panel-connected {
          background: #ab8d80 !important;
          border: 2px solid #ffffff !important;
      }

       #pseudo-container > .blue {
       	 /*fondo de la etiqueta del nombre*/
                background : #923B3B !important ;
                box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
       }
       .title-container a {
       	/*nombre del avatar*/
                color : #FFFFFF !important ;
                text-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
       }
    	/*fondo del episodio*/
        #episode-container {
              background: #FCDFB9 !important;
            border: 2px solid #ffffff !important;
        }
        /*color del nombre del episodio*/
        #episode-container span {color : #9B7400 !important ;}
        /*numero del episodio*/
        #episode-container .number {color : #FFFFFF !important ;}
    	/*clip*/
       .trombone {background-image : url("http://www.amoursucre.com/image/paper-clip/left.png") !important ;}
    
        #currencies-container .values {
        	/*fondo de PA's y dinero*/
           background : #C3A8A1 !important ;
           /*borde*/
           border : 2px solid #FFFFFF !important ;
           /*Color de las letras*/
           color : #FFFFFF !important ;
        }
    	/*menu Perfil-Banco-Foro-ayuda*/
        #panel-menu a {
            background : #923B3B !important ;
            /*color de letras*/
            color : #FFFFFF !important ;
            /*BORDE*/
            border: 2px solid #ffffff !important;
        }
      
        /*menu Piso-Amigos-Ciudad*/
    navigation-menu > div > .pink, navigation-menu > div > .blue {
        background : #DFAF1F !important ;
        border-color: #FFFFFF;
        /*color de letras*/
        color : #FFFFFF !important ;
        border: 2px solid #FFFFFF !important;
    }

    navigation-menu > div > .border {
        border : none !important ;
    }

    /*************************************************** PAGINA PRINCIPAL ****************************************************************/
    /*Color en las pestañas izquierdas - Face - twetter */
    side-links > .link-entry {
          background : #923B3B !important ;
          border: 2px solid #FFFFFF !important;
    }
    /*color de fondo de la pestaña de mis objetivos*/
    [_nghost-c7] .outer-button[_ngcontent-c7] button[_ngcontent-c7]{
      background: #9B7400;
      border: 2px solid #FFFFFF !important;
    }
    /*color de fondo de la pestaña mis objetivos despues de darle clic*/
    [_nghost-c7]   .outer-button[_ngcontent-c7]   button.front[_ngcontent-c7]{
      background: #923B3B;
      border: 2px solid #FFFFFF !important;
    }
    /*color de fondo del cuadro de mando*/
    [_nghost-c50] .title[_ngcontent-c50] {
      background: #8A0B0B;
      box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
    }
    /*color de la etiqueta del resumen del episodio en la pagina principal*/
    [_nghost-c55] .header[_ngcontent-c55] .title[_ngcontent-c55] {
      background: #BC2222;
      box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
    }
    /*color de la etiqueta del nombre en la pagina principal*/
    [_nghost-c53] .right .player-name {
      background: #923B3B;
      box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
    }
    /*color de los botones mi perfil y mi apariencia de la pagina principal*/
    [_nghost-c53] .right .buttons a {
          background: #f9d464;
          border: 2px solid #dfaf1f !important;
    }
    /*color del numero del episodio*/
    [_nghost-c14] .number[_ngcontent-c14] {
        color: #dfaf1f;
    }
    /*Texto Mini juegos*/
    [_nghost-c50] .left[_ngcontent-c50] .minigame-box[_ngcontent-c50] {
         color: #dfaf1f;
    }
    /*Color del boton JUGAR*/
    [_nghost-c50] .left[_ngcontent-c50] .minigame-box[_ngcontent-c50] a[_ngcontent-c50] {
    background: #923b3b;
    }
    /*color del boton JUGAR al poner el mouse encima*/
    [_nghost-c50] .left[_ngcontent-c50] .minigame-box[_ngcontent-c50] a[_ngcontent-c50]:hover {
    background: #8a0b0b;
    }   
    /*globo donde se muestra el empisodio que se cursa en la pagina principal*/
    [_nghost-c57]     .inner-episode-cover .informations .episode-bubble{
      background-image: url("http://orig12.deviantart.net/50a9/f/2017/179/1/7/home_episode_bubble_by_darknessblack-dbedny9.png"); 
    }
    /*nombre del epidodio que se esta cursando en la pagina principal */
    [_nghost-c57] .inner-episode-cover .informations .episode-title {
          background: #9e8539;
    }
    /*Boton de continuar el episodio actual en la pagina prinipal*/
    [_nghost-c55]   .footer[_ngcontent-c55]   .continue[_ngcontent-c55]{
       background: #f9d464;
          border: 2px solid #dfaf1f !important;
    }
    /*etiqueta Foro en la pagina principal*/
    [_nghost-c41] .news-header>span{
      background: #8a0b0b;
    }
    /* Noticias nuevas fondo y color de letra*/
    [_nghost-c41] .news-outer-body .news-inner-body .news-entry .news-date {
      color: #fff;
      background: url(http://orig12.deviantart.net/ceb0/f/2017/179/8/4/date_background_by_darknessblack-dbednyl.png) no-repeat;
    }
    /*color de las letras de las ultimas noticias*/
    [_nghost-c41] .news-outer-body .news-inner-body .news-entry .news-subject {
      color: #9B7400 ;
    }
    /*Etiqueta de Articulos Derivados de Corazon de Melon*/
    [_nghost-c52] header[_ngcontent-c52] .title[_ngcontent-c52] {
      background: #dfaf1f;
    }
    /*etiqueta de ver mas en la pagina principal*/
    [_nghost-c41] .news-outer-body .news-inner-body .show-more a{
      background: #8A0B0B;
    }
    /*subfondo de la pagina principal*/
    [_nghost-c50] {
      background-image: url("http://orig09.deviantart.net/53ca/f/2017/179/3/d/dashboard_background_by_darknessblack-dbednzh.jpg"); 
    }
    /*subfondo inferior*/
    [_nghost-c49] .home-bottom[_ngcontent-c49] {
      background-image: url("http://orig13.deviantart.net/4645/f/2017/179/f/0/bottom_zone_by_darknessblack-dbedort.jpg");
    }
    /**********************************************  PERFIL DE SUCRETTE *******************************************************************/
    /*Fondo de la etiqueta del nombre en el perfil del usuario*/
    .blue[_nghost-c13]{
          background: #923B3B;
    }
    /*fondo en las opciones Mi perfil - Mi cuenta, dentro del perfil del usuario */
        [_nghost-c22] .container .right .tabs .tab  {
          background: #923B3B;
    }
    /*Color de letra en las opciones Mi perfil - Mi cuenta, dentro del perfil de usuario*/
    [_nghost-c22] .container .right .tabs .tab.active {
          color: #923B3B;
    }
    /*tarjeta de men¡mbrecia del instituto*/
      .member[_nghost-c61] {
          background-image: url("http://orig12.deviantart.net/83ba/f/2017/179/8/8/profile_card_by_darknessblack-dbeclcp.png") !important;
    }
    /*color de fondo de la informacion de la mmbrecia*/
    [_nghost-c61] table tr td.label {
        background: hsla(0,0%,100%,.8);
    }
    /*Color del boton Editar firma, dentro del perfil de usuario */
    [_nghost-c60] .wall .wall-title .edit-wall {
      background: #923B3B;
    }
    /*Color de las letras del Muro del usuario*/
    [_nghost-c60] .wall .wall-title {
      color: #9B7400;
    }
    /*fondo del avatar*/
    [_nghost-c24] .profile-avatar-container {
      background-image: url("http://orig15.deviantart.net/ba4f/f/2017/179/d/e/avatar_box_by_darknessblack-dbeclcn.png");
      -moz-box-shadow: 0px 0px 30px #FF0F0F; 
      -webkit-box-shadow: 0px 0px 30px #FF0F0F; 
      box-shadow: 0px 0px 30px #FF0F0F;
      
      padding: 10px;
      border: 1px solid #880505;
      
      margin: 40px;
    }
    /*Color del texto MIS EPISODIOS*/
    [_nghost-c23] >h2 {
      color: #923B3B;
    }
    /*color de la etiqueta resumen del episodio actual*/
    .pink[_nghost-c13] {
      background: #FCD791;
    }
    /*color de la etiqueta del episodio en donde se encuentra el usuario*/
    [_nghost-c72] .body .inner-body .informations .episode-title {
      background: #923B3B;
    }
    /*color del boton de los episodio pasados para volverlos a jugar*/
    [_nghost-c78]   .start-replay-button[_ngcontent-c78]   button[_ngcontent-c78]{
      background: #FCD791;
    }
    /*color de las etiquetas de los episodio pasados*/
    [_nghost-c73] .body[_ngcontent-c73] .inner-body[_ngcontent-c73] .informations[_ngcontent-c73] .top-informations[_ngcontent-c73] .episode-title[_ngcontent-c73]{
      background: #923B3B;
    }
    /*color del boton de continuar el episodio*/
    [_nghost-c72] .body .bottom-body>div a.button {
      background: #9B7400;
    }
    /*color de fondo en las opciones en mis coqueteos*/
    [_nghost-c86] .tabs .tab {
      background: #923B3B;
    }
    /*colo de letra en las opciones de mis coqueteos*/
    [_nghost-c86] .tabs .tab.active {
      color: #923B3B;
    }
    /*Cursor*/
    body, a, a:hover {
       cursor:url(http://orig04.deviantart.net/7d31/f/2017/179/1/e/cursor_by_darknessblack-dbec42e.png), auto;
    }
}
