css:
@-moz-document domain("www.runscope.com") {
.dashboard-header {
  color: #ececec !important;
  background-color: #1e1e1e !important;
  border-color: #1e1e1e !important; }

html, #content, .container-fluid, main-content, .stream-container {
  color: #ececec !important;
  background-color: #292929 !important;
  border-color: #292929 !important; }

.left-nav, .switcher {
  background-color: #2e2e2e !important; }
  .left-nav .nav-pills > .active > a, .left-nav .nav-pills > .active > a:hover, .switcher .nav-pills > .active > a, .switcher .nav-pills > .active > a:hover {
    color: #ececec !important;
    background-color: #1e1e1e !important;
    border-color: #1e1e1e !important; }

#stream_update_notifier {
  background-color: #424242 !important;
  border-color: #ff9800 !important;
  color: #ff9800 !important; }

.tc-gs {
  color: #ececec !important;
  background-color: #424242 !important;
  border-color: #424242 !important; }

.dashboard-card-hover {
  color: #ececec !important;
  background-color: #2e2e2e !important;
  border-color: #2e2e2e !important; }

.tl-layout > * {
  color: #ececec !important;
  background-color: #292929 !important;
  border-color: #292929 !important; }

.walkthrough-body {
  background-color: #2e2e2e !important; }

.message-wrapper {
  background-color: #2e2e2e !important;
  border-top: 1px solid #2e2e2e !important;
  border-right: 1px solid #2e2e2e !important;
  border-bottom: 1px solid #2e2e2e !important; }

#stream_load_more {
  color: #ececec !important;
  background-color: #2e2e2e !important;
  border-color: #2e2e2e !important; }

.formattedJson .k {
  color: #ececec !important; }

.formattedJson .kvov {
  color: #c9c9c9 !important; }

.formattedJson .n {
  color: #2196F3 !important; }

.formattedJson .bl {
  color: #2196F3 !important; }

.formattedJson .e {
  filter: invert(100%); }

.formattedJson .blockInner {
  border-left: 1px dotted rgba(82, 82, 82, 0.31); }

.formattedJson a:link {
  color: #393 !important; }

.hljs, pre, code, pre code {
  color: #ececec !important;
  background-color: #424242 !important;
  border-color: #424242 !important; }

.hljs-keyword {
  color: #2196F3 !important; }

.hljs-string {
  color: #F44336 !important; }

.hljs-title {
  color: #106ebe !important; }

*[class^="hl-code-"] {
  color: black !important; }

}
