css:
@-moz-document domain("web.whatsapp.com") {
/* Based in g/styles/138918/dark-blue-chat-heads-privacy */

@media (max-width: 60em) {
    .app .pane-two {
        width: 100%;
    }
    .media-collection {
        margin-top: 0px;
    }
    .pane-chat-footer {
        border-left: 0px;
    }
    div.pane-one:not(:hover) {
        width: 76px;
        -webkit-transition: width 0.5s;
        /* Safari */
        
        transition: width 0.5s;
    }
    div.pane-one:not(:hover) .pane-list-controls {
        display: none;
        opacity: 0;
    }
    .pane-list-controls {
        display: inline;
        opacity: 1;
    }
    div.pane-one:hover .pane-list-controls {
        -webkit-transition: opacity 2.5s;
        transition: opacity 2.5s;
    }
    div.pane-one:hover {
        -webkit-transition: width 0.5s;
        -webkit-transition-delay: 0.3s;
        transition: width 0.5s;
        transition-delay: 0.3s;
        width: 100%;
    }
    #main,
    .pane-intro {
        width: inherit !important;
    }
    .text-input {
        position: relative;
    }
    .input-spacer {
        padding-top: 15px;
        padding-bottom: 15px;
    }
    .popup-controls-item {
        transition: box-shadow .18s ease-out, background .18s ease-out;
    }
    .well-chat-secondary {
        margin-top: 5px;
    }
    .row {
        margin-bottom: 0 !important;
    }
    .drawer-section-body {
        padding-top: 0px !important;
    }
    .drawer-section-title,
    .drawer-section-body {
        margin-bottom: 0px;
    }
    .row {
        margin-bottom: 0px;
    }
    .cont-input-search {
        border: 0px !important;
    }
    .media-viewer-thumbs-container {
        margin-top: 0px;
    }
    .pane-intro:after {
        border: 0px;
    }
    .butterbar-notification {
        height: 50px;
    }
    .intro-image {
        border-radius: 180px;
    }
    .icon-search,
    .icon-smiley,
    .icon-status,
    .icon-ptt {
        border-radius: 30px;
    }
    .icon-send {
        border-radius: 5px;
    }
    .tail-container {
        opacity: 0;
    }
    .bubble-image {
        border-radius: 5px;
    }
    .input-emoji {
        border-radius: 5px;
        padding-left: 8px;
    }
    .msg {
        padding-left: 12px;
    }
    .chatlist .chat-avatar {
        padding: 0 0 0 6px;
    }
    .app {
        min-width: 0 !important;
    }
    .unread-count {
        margin-left: -60px;
        margin-top: 23px;
    }
    span.pane.pane-one {
        width: 300% !important;
    }
}
}
