css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vk.com") {
    @keyframes mymove {
    0%   {top: 50%;}
    100% {top: 30%;}
    }
.fidget_spinner,
.im-page--history.im-page--history_loading:before {
    position: absolute;
    top: 50%;
    left: 50%;
    z-index: 1000;
    display: block;
    background: url('https://s2.nofilecdn.io/g/vGtR7957p9Pyzu0KUQbeo1fJciEUgSbkuhY52zX5kAjrNN3IiAC3cbw03jzMH9oG/huy_spinner.svg');
    width: 54px;
    height: 54px;
    animation: mymove 0.5s infinite forwards
}
}
