css:
@-moz-document domain("newsnow.co.uk") {
#myrecent {
    z-index: 0 !important;
}
.nw_header,
table.nw_header {
    display: none !important;
    white-space: nowrap !important;
}
tr.banner {
    height: 52px !important;
}
tr.banner td.logo a img {
    margin-top: 6px !important;
}
table.banner {
    width: 100% !important;
    height: 42px !important;
}
.banner {
    margin-bottom: 4px !important;
}
.lyt_footer {
    padding-top: 2px !important;
    padding-bottom: 32px !important;
}
.lyt_footer .inner {
    padding-top: 0px !important;
}
table.tp_header,
.navbarsky,
td#ad_bnr,
#ad_report_1_content,
td#ad_report_1.ad_report_btn.ad_report_btn_v,
#ad_report_btn.ad_report_btn_v,
#ad_report.ad_report_btn,
#ad_report_1.ad_report_btn,
#ad_report_2.ad_report_btn,
#ad_report_3.ad_report_btn,
#ad_report_4.ad_report_btn,
#ad_mpu,
#Ad_1,
#Ad_2,
#Ad_3,
#Ad_4,
#pubfilter,
header.teaserlike,
.newsfeed.newsfeed_cld.f_12.b_off,
.lyt_sidebar > #neutral.header:nth-of-type(1),
.lyt_sidebar > #neutral.box:nth-of-type(2),
#newswire > #neutral.header.teaserlike,
.navbar_l > #neutral.header:nth-of-type(3),
.navbar_l > .dynarch-popup-menu.dynarch-popup-base-menu.hmenu:nth-of-type(6),
.feedback_bar,
.switch_placeholder,
#ad_2 {
    display: none !important;
}
body {
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 12px;
    color: #989696;
}
html,
body,
#pwrap {
    position: relative;
    margin: 0;
    padding: 0;
    height: 100%;
    background: #1B1A1A!important;
}
.newsfeed .hl .hll {
    color: #AFADAD;
    border: 0 none;
}
.newsteaser .newsbox_inner,
#sidebar .box .boxc {
    padding: 8px 0 12px;
    background-color: #151313;
}
element.style {
    position: fixed;
    left: 0px;
    top: 0px;
    right: 0px;
    bottom: 0px;
    z-index: -1999999999;
    display: block;
    background: #1B1A1A!important;
}
td {
    background: #151313;
}
TD.END {
    BACKGROUND: 151313;
}
#ptop {
    background: #151313;
}
#pmbar .alertsscroll_wrap {
    background: #151313;
}
#pmbar .alertsscroll ul.alerts li a,
#pmbar .alertsscroll ul.alerts li a:visited {
    color: #FFFFFF;
}

.alertsscroll_container {
    background-color: #000 !important;    
}
TD.label {
    color: #CECACA;
}
body .newsbox_s_n .feed_title,
.pfeed_s_n .page_title_inner,
body .page_title_s_n .page_title_inner {
    background-color: #860E0E;
}
body .newsbox_s_sp .feed_title,
.pfeed_s_sp .page_title_inner,
body .page_title_s_sp .page_title_inner {
    background-color: #67842C;
}
#phead .nn_topbar {
    height: 40px;
    padding: 0 0;
    background: #67842C;
}
body .newsbox_s_sp .feed_title .bullet,
.pfeed_s_sp .page_title_inner .bullet,
body .page_title_s_sp .page_title_inner .bullet {
    background-color: #8BAB4C;
}
.newsbox_s_sp .tabselector ul li.active a,
.newsbox_s_sp .tabselector ul li.active a:hover .pfeed_s_sp .tabselector ul li.active a,
.pfeed_s_sp .tabselector ul li.active a:hover {
    border-color: #85ad33 #85ad33 #082027 #85ad33;
    color: #85ad33;
}
.pfeed .tabselector ul li.active a,
.pfeed .tabselector ul li.active a:hover {
    color: #6699CC;
    border: 1px solid #CCCCCC;
    border-bottom: 1px solid #082027;
    background: #082027;
}
#content div.h1:hover {
    background-color: #1B1A1A!important;
}
.newsfeed .hl a.hll:hover,
.newsfeed .hl a.hll:active,
.newsfeed .hl a.hll:focus {
    background-color: #272F16;
}

input.q {
    color: #fff !important;
}

.c-savethelink-promo--boxed{
    display:none !important;
}
}
