css:
@-moz-document domain("steemit.com") {
  
  .App__content
  {
   margin-top: 3.5rem;
  }
  
  .PostsList__post_container .PostFull__footer{
    padding: 0.5rem 0;
    border-bottom: 1px solid hsl(0, 0%, 90.2%);
    background: white;
    position:fixed;
    top: 0px;
    left: 50%;
    width: 61rem;
    margin-left: -30.5rem;
    margin-top: 3rem;
  }
  
  .PostsList__post_container .PostFull {
    margin-top: 3rem;
  }
  
  .App__content > .Post .PostFull__footer {
    position: fixed;
    width: 100%;
    left: 50%;
    margin-left: -50%;
    top: 5.2rem;
    padding: 0.5rem 0;
    border-bottom: 1px solid hsl(0, 0%, 90.2%);
    background: white;
  }
  
  .PostFull__reply{
   position: fixed;
   bottom: 0px;
   right: 1rem;
   border-right: none;
   background: white;
   padding: 0.3rem;
   border-radius: 0.7rem;
  }
  
  .PostFull__reply a:last-of-type{
    border-left: 1px solid;
    padding-left:0.5rem;
  }
  
  @media all and (max-width: 1071px) {
    .PostsList__post_container .PostFull__footer {
     width: 56rem;
     margin-left: -28rem;
    }
  }
  
  @media all and (max-width: 960px) {
    .PostsList__post_container .PostFull__footer {
     width: 100%;
     margin-left: -50%;
    }
  }

  @media all and (max-width: 640px) {
    .App__content > .Post .PostFull__footer {
     top: 3rem;
    }
  }
}
