@-moz-document domain("ceruleonice.wordpress.com")
{
  /* Main Background */
  body,
  .hfeed.site
  {
    background-color: #1a1a1a !important;
  }
  /* Text/Reading Area */
  main.site-main,
  .hentry,
  header.entry-header,
  div.entry-content
  {
    background-color: #232323 !important;
    color: #eee !important;
    font-size: 18.5px !important;
    font-family: 'Open Sans', sans-serif !important;
  }
  /* Menu Bar */
  div.navigation-bar.clear,
  div.navigation-bar.clear ul li
  {
    background-color: #232323 !important;
  }
  /*Removed a annyoing thing that lagged the page out slightly*/
  a.to-top
  {
    display: none !important;
    opacity: 0 !important;
  }
  /*Removed image above Text/Reading area*/
  figure.entry-featured-image
  {
    /*[[rmFI]]*/;
  }
  /* Comments Area */
  div.comments-area
  {
    background-color: #202020 !important;
  }
