css:
@-moz-document domain("pandora.com")
{
/* Remove Ad Container */

.DisplayAdController
{
    display: none !important;
}
/* Full Width Page */

.region-main--rightRail,
.region-bottomBar--rightRail,
.region-topBar--rightRail,
.MiniBar--rightRail
{
    width: 100% !important;
}
/* Center Background Album Art */

.ArtBg svg
{
    position: relative !important;
    top: -170px !important;
    width: calc(100% + 340px) !important;
    height: calc(100% + 340px) !important;
}
/* Center Search */

.SearchModal--rightRail
{
    position: fixed !important;
    left: 50% !important;
    margin-left: -22.5rem !important;
    width: 45rem !important;
}

.Tuner__Audio__TrackDetail,
.Tuner__VolumeDurationControl
{
    width: 25% !important;
}
}
